import React from 'react';
import IMessage from '../alias/IMessage';
import { useGroup } from '../contexts/GroupContext';
import { useContact } from '../contexts/ContactContext';

interface IGroupNotification{
    msg: IMessage;
}


const GroupNotification : React.FC<IGroupNotification> = ({ msg }) => {
    let userRemoving: any;
    const { groups, removedMember } = useGroup();
    const { contacts, contactsOutsideUserContacts } = useContact();
    switch(msg.eventId){
        case 6:
        groups.map((grp) => {
            if(grp.groupId === msg.fromUser){ //msg.fromUser é o id do grupo que ocorrerá o evento?
                contacts.map((ctc) => {
                    if(ctc.id === msg.eventBody){
                        userRemoving = msg.eventBody;
                    }
                })
                contactsOutsideUserContacts.map((ctc) => {
                    if(ctc.id === msg.eventBody){
                        userRemoving = msg.eventBody;
                    }
                })
            }
        })
        return(
            <div className="card">
                <div className="card-body" style={{color: "red"}}>
                    {removedMember?.name} has been removed by {userRemoving}!
                </div>
            </div>
        );
        default:
            return <></>    
    }
}

export default GroupNotification;
