import SquadChatCommunicator from "./SquadChatCommunicator";
import API from "@digivox/apicommunicator";
import { SubscriptionCallBack } from "./types";
import { SubscriptionCallBack as VoiceCB } from "./voice/types/types";
import { getAuthorization } from "./utils/localStorageUtils";
import SettingsBusiness from "./business/Settings";
import ContactsBusiness from "./business/Contacts";
import GroupsBusiness from "./business/Groups";
import ChatsBusiness from "./business/Chats";
import MessagesBusiness from "./business/Messages";
import UserBusiness from "./business/User";
import AttachmentsBusiness from "./business/Attachments";
import SquadVoiceCommunicator from "./SquadVoiceCommunicator";
import ConferencesBusiness from "./business/Conferences";
import SquadEventsCommunicator, {
  ConferenceEvent,
  ConferenceEvents,
} from "./SquadEventsCommunicator";
interface CommicatorCallbacks {
  chatCommunicatorSubscribe?: SubscriptionCallBack;
  voiceCB?: VoiceCB;
}
export interface SquadCommunicator {
  interfaceChatCommunicatorSubscribe: SubscriptionCallBack;
  interfaceVoiceCommunicatorSubscribe: VoiceCB;
  getInstance(): SquadCommunicator;
  api: API | undefined;
  chat: SquadChatCommunicator | undefined;
  settings: SettingsBusiness;
  contacts: ContactsBusiness;
  groups: GroupsBusiness;
  chats: ChatsBusiness;
  conferences: ConferencesBusiness;
  messages: MessagesBusiness;
  user: UserBusiness;
  attachments: AttachmentsBusiness;
  sip: SquadVoiceCommunicator;
  events: SquadEventsCommunicator;
}
export class SquadCommunicator {
  private static instance: SquadCommunicator | undefined;
  static getInstance(callbacks: CommicatorCallbacks) {
    if (!this.instance) {
      this.instance = new SquadCommunicator();
      this.instance.makeAPI();
    }
    callbacks.chatCommunicatorSubscribe &&
      this.instance.updateChatSubscribeFuncition(
        callbacks.chatCommunicatorSubscribe
      );
    callbacks.voiceCB &&
      this.instance.updateVoiceSubscribeFunction(callbacks.voiceCB);
    return this.instance;
  }
  updateChatSubscribeFuncition(
    chatCommunicatorSubscribe: SubscriptionCallBack
  ) {
    this.chat?.removeAllSubscription();
    this.chat?.subscribe(chatCommunicatorSubscribe);
  }
  updateVoiceSubscribeFunction(voiceCB: VoiceCB) {
    this.sip?.removeAllSubscription();
    this.sip?.subscribe(voiceCB);
  }
  updateEventsSubscribeFunction(
    eventsCB: (event: ConferenceEvents, data: ConferenceEvent) => void
  ) {
    this.events.updateEventCallback(eventsCB);
  }
  // -- Make Services --
  makeAPI() {
    const authorization = getAuthorization();
    if (authorization) {
      const api = new API({
        token: authorization.access_token,
        apiUrl: authorization.baseUrl,
      });
      this.api = api;
      this.makeBusiness();
    }
  }
  makeBusiness() {
    if (!this.api) return;
    this.settings = new SettingsBusiness(this.api);
    this.user = new UserBusiness(this.api, this.settings);
    this.contacts = new ContactsBusiness(this.api, this.settings);
    this.groups = new GroupsBusiness(this.api, this.settings, this.contacts);
    this.conferences = new ConferencesBusiness(this.api, this.settings);
    this.events = new SquadEventsCommunicator(this.settings);
    this.chats = new ChatsBusiness(
      this.api,
      this.contacts,
      this.groups,
      this.user
    );
    this.messages = new MessagesBusiness(
      this.api,
      this.contacts,
      this.groups,
      this.user
    );
    this.attachments = new AttachmentsBusiness(this.api, this.settings);
    this.chat = new SquadChatCommunicator(
      this.settings,
      this.contacts,
      this.groups,
      this.user,
      this.interfaceChatCommunicatorSubscribe
    );
    this.contacts.setChat(this.chat);
    this.groups.setChat(this.chat);
    this.sip = new SquadVoiceCommunicator(
      this.settings,
      this.user,
      this.interfaceVoiceCommunicatorSubscribe
    );
  }
}
