import React from "react";
import { Link } from "react-router-dom";
import { Media } from "reactstrap";
import SimpleBar from "simplebar-react";
import { useChat } from "../contexts/ChatContext";
import { useContact } from "../contexts/ContactContext";
const FavoriteMessagesTab = () => {
  const {favoriteMessages} = useChat();
  const {contacts, contactsOutsideUserContacts} = useContact();
  return (
    <React.Fragment>
      <div className="p-4">
        <SimpleBar
          style={{ maxHeight: "100%" }}
          className="p-4 chat-message-list chat-group-list"
        >
          <ul className="list-unstyled chat-list">
            <h4 className="">Favorite messages</h4>
            {favoriteMessages?.map((item, key) => (
              <li key={key}>
                <Link to="#" /* onClick={() => openConference(item)} */ >
                  <Media className="align-items-center">
                    <div className="chat-user-img mr-3">
                      <div className="avatar-xs">
                        <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                          â˜…  
                        </span>
                      </div>
                    </div>
                    <Media body className="overflow-hidden">
                      <h5 className="font-size-14 mb-0">
                      {item.message}
                      </h5>
                      <p className="chat-user-message mb-0">
                        {item.messageDate}
                      </p>
                      <p className="chat-user-message mb-0">
                        {contacts.map((ctc) => {
                          if(ctc.jid === item.id){
                            return <div>{ctc.name}</div>
                          }
                        })}
                        {contactsOutsideUserContacts.map((ctc) => {
                          if(ctc.jid === item.id){
                            return <div>{ctc.name}</div>
                          }
                        })}
                      </p>
                    </Media>
                  </Media>
                </Link>
              </li>
            ))}
          </ul>
        </SimpleBar>
      </div>
    </React.Fragment>
  );
}

export default FavoriteMessagesTab;
