import React, { memo, useCallback } from "react";
import {
  RiMicLine,
  RiCameraLine,
  RiMicOffLine,
  RiCameraOffLine,
} from "react-icons/ri";
import { MdScreenShare, MdStopScreenShare } from "react-icons/md";
import IConferenceMember from "./../alias/IConferenceMember";
interface IProps {
  participant: IConferenceMember;
}

const ParticipantItem: React.FC<IProps> = ({ participant }) => {
  const formatLabelAvatar = useCallback(() => {
    return participant.callerName.charAt(0).toUpperCase();
  }, [participant.callerName]);

  const mic = participant.mute ? "on" : "off";
  const camera = participant.video ? "on" : "off";
  const name = participant.callerName;
  const avatar = undefined;
  const talking = participant.talking ? "on" : "off";
  const screenShare = false ? "on" : "off";

  return (
    <div className="participants-item">
      <div className="participant-info">
        <div className="participant-avatar">
          {avatar !== undefined ? (
            <img src={avatar} alt="participant" />
          ) : (
            <span className="participant-avatar">{formatLabelAvatar()}</span>
          )}
          <span className={`participant-status ${""}`} />
        </div>
        <strong>{name}</strong>
      </div>
      <div className="participant-actions">
        {mic === "off" ? <RiMicOffLine className="off" /> : <RiMicLine />}
        {camera === "off" ? (
          <RiCameraOffLine className="off" />
        ) : (
          <RiCameraLine />
        )}
        {talking === "off" ? (
          <i className="ri-volume-down-line" />
        ) : (
          <i className="ri-volume-up-line off" />
        )}
        {screenShare === "off" ? (
          <MdStopScreenShare className="off" />
        ) : (
          <MdScreenShare />
        )}
      </div>
    </div>
  );
};

export default memo(ParticipantItem);
