import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
} from "reactstrap";

//simple bar
import SimpleBar from "simplebar-react";

//components
import SelectContact from "./SelectContact";

import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import { useContact } from "../contexts/ContactContext";
import EmailList from "./EmailList";

interface IProps {
  show: boolean;
  toggle(): void;
}

export const ModalNewConference: React.FC<IProps> = ({ show, toggle }) => {
  const { t } = useTranslation();
  const { contacts } = useContact();

  const [isOpenAlert, setIsOpenAlert] = useState(false);
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");

  const [startDate, setStartDate] = useState<string>("");
  const [endDate, setEndDate] = useState<string>("");
  const [startTime, setStartTime] = useState<string>("");
  const [endTime, setEndTime] = useState<string>("");

  const [emailList, setEmailList] = useState<string[]>([]);

  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);

  const handleCreateGroup = () => {
    console.log(
      JSON.stringify({
        startDate,
        endDate,
        startTime,
        endTime,
        selectedContact,
        emailList,
      })
    );

    setIsOpenAlert(true);
    setMessage("Criando conferência...");

    setTimeout(() => {
      setIsOpenAlert(false);
      setMessage("");
      setSearch("");
    }, 2000);
  };

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      let selected = selectedContact;
      selected.push(contact);
      setSelectedContact(selected);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };

  const today = () => {
    var today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth() + 1; //January is 0!
    var yyyy = today.getFullYear();

    return `${yyyy}-${mm}-${dd}`;
  };

  return (
    <Modal isOpen={show} centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        {t("Create conference")}
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <div className="row">
            <FormGroup className="mb-4 col-6">
              <Label htmlFor="date-start-input">{t("Date Start")}</Label>
              <Input
                type="date"
                className="form-control"
                id="date-start-input"
                min={today()}
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
              />
            </FormGroup>
            <FormGroup className="mb-4 col-6">
              <Label htmlFor="date-end-input">{t("Date End")}</Label>
              <Input
                type="date"
                className="form-control"
                id="date-end-input"
                disabled={startDate === ""}
                min={startDate}
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
              />
            </FormGroup>
          </div>
          <div className="row">
            <FormGroup className="mb-4 col-6">
              <Label htmlFor="time-start-input">{t("Date Start")}</Label>
              <Input
                type="time"
                className="form-control"
                id="time-start-input"
                value={startTime}
                onChange={(e) => setStartTime(e.target.value)}
              />
            </FormGroup>
            <FormGroup className="mb-4 col-6">
              <Label htmlFor="time-end-input">{t("Date End")}</Label>
              <Input
                type="time"
                className="form-control"
                id="time-end-input"
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
              />
            </FormGroup>
          </div>
          <FormGroup className="mb-4">
            <Label htmlFor="search-input">{t("Conference Members")}</Label>
            <Input
              type="text"
              className="form-control mb-2"
              id="search-input"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder={t("Search users..")}
            />
            <div className="border">
              <SimpleBar style={{ height: "250px" }}>
                <div id="addContacts">
                  <SelectContact
                    handleCheckContact={handleCheckContact}
                    contacts={contacts}
                    search={search}
                  />
                </div>
              </SimpleBar>
            </div>
          </FormGroup>
        </Form>
        <EmailList emailList={emailList} setEmailList={setEmailList} />
        <Alert isOpen={isOpenAlert} color="danger">
          {message}
        </Alert>
      </ModalBody>
      <ModalFooter>
        <Button type="button" color="link" onClick={toggle}>
          {t("Close")}
        </Button>
        <Button type="button" color="primary" onClick={handleCreateGroup}>
          {t("Create conference")}
        </Button>
      </ModalFooter>
    </Modal>
  );
};
