import React from "react";
import SimpleBar from "simplebar-react";
import {
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Media,
} from "reactstrap";

import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import sortContactGroup from "../helpers/SortContactGroup";

interface IProps {
  recentContactList: IContact[];
}

const ContactList: React.FC<IProps> = ({ recentContactList }) => {
  const { t } = useTranslation();

  return (
    <SimpleBar
      style={{ maxHeight: "100%" }}
      id="chat-room"
      className="p-4 chat-message-list chat-group-list"
    >
      {sortContactGroup(recentContactList).map((contact, key) => (
        <div key={key} className={key + 1 === 1 ? "" : "mt-3"}>
          <div className="p-3 font-weight-bold text-primary">
            {contact.group}
          </div>

          <ul className="list-unstyled contact-list">
            {contact.children.map((child, key) => (
              <li key={key}>
                <Media className="align-items-center">
                  <Media body>
                    <h5 className="font-size-14 m-0">{child.name}</h5>
                  </Media>
                  <UncontrolledDropdown>
                    <DropdownToggle tag="a" className="text-muted">
                      <i className="ri-more-2-fill"></i>
                    </DropdownToggle>
                    <DropdownMenu right>
                      <DropdownItem>
                        {t("Share")}{" "}
                        <i className="ri-share-line float-right text-muted"></i>
                      </DropdownItem>
                      <DropdownItem>
                        {t("Block")}{" "}
                        <i className="ri-forbid-line float-right text-muted"></i>
                      </DropdownItem>
                      <DropdownItem>
                        {t("Remove")}{" "}
                        <i className="ri-delete-bin-line float-right text-muted"></i>
                      </DropdownItem>
                    </DropdownMenu>
                  </UncontrolledDropdown>
                </Media>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </SimpleBar>
  );
};

export default ContactList;
