import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  Input,
  ModalFooter,
} from "reactstrap";

import { useModal } from "../contexts/ModalContext";
import { useContact } from './../contexts/ContactContext'
import { useChat } from "../contexts/ChatContext";
import { useGroup } from "../contexts/GroupContext";
import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import SimpleBar from "simplebar-react";
import SelectMember from "./SelectMember";

const ModalAddContacts: React.FC = () => {
  const [search, setSearch] = useState("");
  const { activeChat } = useChat();
  const { groups } = useGroup();

  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);

  const { t } = useTranslation();
  const { toggleAddingMembers, setToggleAddingMembers, setCheckedMembers, setGroupToInsertMember } = useContact();

  const {
    showModalAddMember,
    setShowModalAddMember,
  } = useModal();

  const toggle = () => {
    if (showModalAddMember) {
      setShowModalAddMember(!showModalAddMember);
    }
  };

  const handleCheckContact = (
    selected: boolean,
    contact: IContact
  ) => {
    if (selected) {
      let selected = selectedContact;
      selected.push(contact);
      setSelectedContact(selected);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };

  return (
    <Modal
      backdrop="static"
      isOpen={!!showModalAddMember}
      centered
      toggle={toggle}
    >
      <ModalHeader toggle={toggle}>{t("Add Contacts")}</ModalHeader>
      <ModalBody>
        <CardBody className="p-2 border">
          <div className="pt-2 pb-3 border-bottom">
            <Input
              type="text"
              className="form-control mb-3"
              id="addgroupname-input"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder={t("Search users..")}
            />
            <hr />
            <SimpleBar style={{ height: "500px" }}>
              <SelectMember handleCheckContact={handleCheckContact} search={search}/>
            </SimpleBar>
          </div>
          <ModalFooter className="border-0">
            <Button color="default" onClick={() => {
              setCheckedMembers([]);
              toggle();
              }}>
              {t("Close")}
            </Button>
            <Button color="primary" 
            onClick={() => {
              setToggleAddingMembers(true);
              console.log('toggleAddingMembers: ' + JSON.stringify(toggleAddingMembers));
              toggle();
              groups.filter((grp) => {
                if(grp.groupId === activeChat.groupId){
                  setGroupToInsertMember(grp);
                  console.log('grp: ' + JSON.stringify(grp));
                };
                return groups;
              })
              }}>
            {t("Add Contacts")}
            </Button>
          </ModalFooter>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalAddContacts;
