import userEvent from "@testing-library/user-event";
import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  ModalFooter,
  Input
} from "reactstrap";

import { useModal } from "./../contexts/ModalContext";
import { useAuth } from './../contexts/AuthContext';
const ModalEditInfo: React.FC = () => {
  const {
    showModalEditInfo,
    setShowModalEditInfo
  } = useModal();
  const { setChangedUserData } = useAuth();
  const toggle = () => setShowModalEditInfo(!showModalEditInfo);

  return (
    <Modal
      backdrop="static"
      isOpen={showModalEditInfo}
      centered
      toggle={toggle}
    >
      <ModalHeader toggle={toggle}>Edite informações...</ModalHeader>
      <ModalBody>
        <CardBody>
          <form>
          <div className="mt-0">Editar nome</div>
          <Input placeholder="Nome"></Input>  
          <div className="mt-4">Editar e-mail</div>
          <Input placeholder="E-mail"></Input>  

          <ModalFooter className="border-0">
            <Button type="submit" color="default" onClick={toggle}>
              Close
            </Button>
            <Button type="submit" color="primary">
              Confirmar
            </Button>
          </ModalFooter>
          </form>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalEditInfo;
