import React, { createContext, useContext, useState } from "react";
import ICallHistory from "../alias/ICallHistory";

interface ICallHistoryContextData {
  callHistory: ICallHistory[];
  setCallHistory(value: ICallHistory[]): void;
}

export const CallHistoryContext = createContext<ICallHistoryContextData>(
  {} as ICallHistoryContextData
);

export const CallHistoryProvider: React.FC = ({ children }) => {
  const [callHistory, setCallHistory] = useState<ICallHistory[]>([]);

  return (
    <CallHistoryContext.Provider value={{ callHistory, setCallHistory }}>
      {children}
    </CallHistoryContext.Provider>
  );
};

export function useCallHistory() {
  return useContext(CallHistoryContext);
}
