import React, { useState } from "react";
import { Input, InputGroupAddon, InputGroup, Button } from "reactstrap";
import { useTranslation } from "react-i18next";
import CallHistoryList from "./CallHistoryList";
import DialPad from "./DialPad";
import { useModal } from "../contexts/ModalContext";
import { useCallHistory } from "../contexts/CallHistoryContext";
import ICallHistory from "../alias/ICallHistory";
import { useCall } from "../contexts/CallContext";

const CallHistoryTab: React.FC = () => {
  const { callHistory } = useCallHistory();
  const { showModalDialPad, setShowModalDialPad } = useModal();
  const { setBlindTransfer } = useCall();
  const [searchCallHistory, setSearchCallHistory] = useState("");
  const { t } = useTranslation();

  const searchArray = (callHistoryList: ICallHistory[], search: string) => {
    return callHistoryList.filter((item) => {
      return (
        item.number.toUpperCase().match(search.toUpperCase()) ||
        item.name?.toUpperCase().match(search.toUpperCase())
      );
    });
  };

  const toggle = () => {
    if (!showModalDialPad) {
      setBlindTransfer(undefined);
    }
    setShowModalDialPad(!showModalDialPad);
  };

  return (
    <>
      <div>
        <div className="px-4 pt-4">
          <h4 className="mb-4">{t("Call History")}</h4>
          <div className="search-box chat-search-box">
            <InputGroup size="lg" className="mb-3 bg-light rounded-lg">
              <InputGroupAddon addonType="prepend">
                <Button
                  color="link"
                  className="text-muted text-decoration-none"
                  type="button"
                  disabled
                >
                  <i className="ri-search-line search-icon font-size-18"></i>
                </Button>
              </InputGroupAddon>
              <Input
                type="text"
                value={searchCallHistory}
                onChange={(e) => setSearchCallHistory(e.target.value)}
                className="form-control bg-light"
                placeholder={t("Search contacts or numbers")}
              />
              <InputGroupAddon addonType="append">
                <Button
                  color="link"
                  className="text-muted text-decoration-none"
                  type="button"
                  onClick={toggle}
                >
                  <i className=" ri-keyboard-line font-size-18"></i>
                </Button>
              </InputGroupAddon>
            </InputGroup>
          </div>
        </div>

        {/* <FavoriteUsers /> */}

        <div className="px-2">
          <h5 className="mb-3 px-3 font-size-16">{t("Recent")}</h5>
          <CallHistoryList
            recentCallHistory={searchArray(callHistory, searchCallHistory)}
          />
        </div>
        <DialPad show={showModalDialPad} toggle={toggle} />
      </div>
    </>
  );
};

export default CallHistoryTab;
