import React, { useState } from "react";
import { Link } from "react-router-dom";
import {
  Nav,
  NavItem,
  NavLink,
  UncontrolledTooltip,
  Dropdown,
  DropdownItem,
  DropdownToggle,
  DropdownMenu,
} from "reactstrap";
import classnames from "classnames";
import { useLayout } from "./../contexts/LayoutContext";
import { useTranslation } from "react-i18next";
import logo from "./../assets/images/logo.png";
import avatar1 from "./../assets/images/users/avatar-1.jpg";
import { useMainBox } from "../contexts/MainBoxContext";
import FornecedoresFavoritos from "./FornecedoresFavoritos";
import ScreenType from "../enuns/ScreenType";

const LeftSidebarMenu: React.FC = () => {
  const { setScreen } = useMainBox();
  const { activeTab, setActiveTab } = useLayout();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [dropdownOpenMobile, setDropdownOpenMobile] = useState(false);
  const toggle = () => setDropdownOpen(!dropdownOpen);
  const toggleMobile = () => setDropdownOpenMobile(!dropdownOpenMobile);
  const logout = () => {
    localStorage.removeItem("authorization");
    localStorage.setItem("logout", "true");
    // eslint-disable-next-line no-restricted-globals
    location.href = `https://${location.hostname}/teste/logout`;
  };
  const toggleTab = (tab: string) => {
    setActiveTab(tab);
  };

  return (
    <React.Fragment>
      <div className="side-menu flex-lg-column">
        <div className="navbar-brand-box">
          <Link to="/" className="logo logo-dark">
            <span className="logo-sm">
              <img src={logo} alt="logo" height="30" />
            </span>
          </Link>

          <Link to="/" className="logo logo-light">
            <span className="logo-sm">
              <img src={logo} alt="logo" height="30" />
            </span>
          </Link>
        </div>

        <div className="flex-lg-column my-auto">
          <Nav
            pills
            className="side-menu-nav justify-content-center"
            role="tablist"
          >
            <NavItem id="Chats">
              <NavLink
                id="pills-chats-tab"
                className={classnames({ active: activeTab === "chats" })}
                onClick={() => {
                  toggleTab("chats");
                }}
              >
                <i className="ri-message-3-line"></i>
              </NavLink>
            </NavItem>
            <UncontrolledTooltip target="Chats" placement="top">
              Chats
            </UncontrolledTooltip>

            <NavItem id="Conferences">
              <NavLink
                id="pills-conferences-tab"
                className={classnames({ active: activeTab === "conferences" })}
                onClick={() => {
                  toggleTab("conferences");
                }}
              >
                <i className="ri-group-2-line"></i>
              </NavLink>
            </NavItem>
            <UncontrolledTooltip target="Conferences" placement="top">
              Conferences
            </UncontrolledTooltip>

            <NavItem id="FornecedoresFavoritos"
               className={classnames({ active: activeTab === "conferences" })}
            >
              
              <NavLink
                onClick={() => {
                  setScreen(ScreenType.FORNECEDORESFAVORITOS);
                }}
              >
                <i className="ri-star-line"></i>
              </NavLink>
            </NavItem>
            <UncontrolledTooltip target="Conferences" placement="top">
              Fornecedores Favoritos
            </UncontrolledTooltip>




            <NavItem id="Settings">
              <NavLink
                id="pills-setting-tab"
                className={classnames({ active: activeTab === "settings" })}
                onClick={() => {
                  toggleTab("settings");
                }}
              >
                <i className="ri-settings-2-line"></i>
              </NavLink>
            </NavItem>
            <UncontrolledTooltip target="Settings" placement="top">
              Settings
            </UncontrolledTooltip>
            <Dropdown
              nav
              isOpen={dropdownOpenMobile}
              toggle={toggleMobile}
              className="profile-user-dropdown d-inline-block d-lg-none"
            >
              <DropdownToggle nav>
                <img
                  src={avatar1}
                  alt="chatvia"
                  className="profile-user rounded-circle"
                />
              </DropdownToggle>
              <DropdownMenu>
                <DropdownItem
                  onClick={() => {
                    toggleTab("profile");
                  }}
                >
                  Profile
                  <i className="ri-profile-line float-right text-muted"></i>
                </DropdownItem>
                <DropdownItem
                  onClick={() => {
                    toggleTab("settings");
                  }}
                >
                  Settings
                  <i className="ri-settings-3-line float-right text-muted"></i>
                </DropdownItem>
                <DropdownItem divider />
                <DropdownItem onClick={logout}>
                  Logout
                  <i className="ri-logout-circle-r-line float-right text-muted"></i>
                </DropdownItem>
              </DropdownMenu>
            </Dropdown>
          </Nav>
        </div>

        <div className="flex-lg-column d-none d-lg-block">
          <Nav className="side-menu-nav justify-content-center">
            <Dropdown
              nav
              isOpen={dropdownOpen}
              className="btn-group dropup profile-user-dropdown"
              toggle={toggle}
            >
              <DropdownToggle nav>
                <img
                  src={avatar1}
                  alt=""
                  className="profile-user rounded-circle"
                />
              </DropdownToggle>
              <DropdownMenu>
                <DropdownItem
                  onClick={() => {
                    toggleTab("profile");
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "center",
                    }}
                  >
                    <i className="ri-profile-line mr-2 text-muted"></i>
                    Profile
                  </div>
                </DropdownItem>
                <DropdownItem
                  onClick={() => {
                    toggleTab("settings");
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "center",
                    }}
                  >
                    <i className="ri-settings-3-line mr-2 text-muted"></i>
                    Settings
                  </div>
                </DropdownItem>
                <DropdownItem divider />
                <DropdownItem onClick={logout}>
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "center",
                    }}
                  >
                    <i className="ri-logout-circle-r-line mr-2 text-muted"></i>
                    Logout"
                  </div>
                </DropdownItem>
              </DropdownMenu>
            </Dropdown>
          </Nav>
        </div>
      </div>
    </React.Fragment>
  );
};

export default LeftSidebarMenu;
