import React from "react";
import { Row, Col } from "reactstrap";
import { useChat } from "../contexts/ChatContext";

interface IProps {
  showChatProfileSidebar: boolean;
}

const ChatInputAnswer: React.FC<IProps> = ({ showChatProfileSidebar }) => {
  const { replyMsg, setReplyMsg } = useChat();

  if (!replyMsg) return <></>;

  return (
    <div
      id="reply-msg"
      className={`p-2 border-top mb-0`}
      style={{ height: "70px" }}
    >
      <Row noGutters>
        <Col sm={10} className="offset-1">
          <div
            style={{
              height: "53px",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
            className={`bg-light pt-1 pb-1 pr-3 pl-3 border-radius`}
          >
            <div>
              <strong>{replyMsg.userName}</strong>
            </div>
            <div>
              <small className="text-muted">{replyMsg.message}</small>
            </div>
          </div>
        </Col>
        <Col
          sm={1}
          className="d-flex justify-content-center align-items-center"
        >
          <div
            className="btn-close-answer"
            onClick={() => setReplyMsg(undefined)}
          >
            <i className="ri-close-line"></i>
          </div>
        </Col>
      </Row>
    </div>
  );
};

export default ChatInputAnswer;
