import React, { useState } from "react";
import { Input, UncontrolledTooltip, Label } from "reactstrap";

interface IProps {
  fileImage: string;
  setFileImage(value: string): void;
}

const ChatInputImagesButton: React.FC<IProps> = ({
  fileImage,
  setFileImage,
}) => {
  const handleImageChange = (e: any) => {
    if (e.target.files.length !== 0)
      setFileImage(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <li className="list-inline-item input-file">
      <Label
        id="images"
        className="mr-1 btn btn-link text-decoration-none font-size-16 btn-lg waves-effect"
      >
        <i className="ri-image-fill"></i>
        <Input
          onChange={(e) => handleImageChange(e)}
          accept="image/*"
          type="file"
          name="fileInput"
        />
      </Label>
      <UncontrolledTooltip target="images" placement="top">
        Images
      </UncontrolledTooltip>
    </li>
  );
};

export default ChatInputImagesButton;
