import React, { useState } from 'react';
import { Button } from 'reactstrap';
import { useChat } from '../contexts/ChatContext';
import { useGroup } from '../contexts/GroupContext';
import Swal from 'sweetalert2';
import { useTranslation } from 'react-i18next';
import ModalGenerics from './ModalGenerics'
import { toast } from 'react-toastify';


interface IProps{
    newGroupName: string;
    setGroupName(value: string): void;
    actualMembers: string[] | undefined;
}

const ChangeGroupNameButton : React.FC<IProps> = ({ newGroupName, setGroupName, actualMembers }) => {
const { activeChat } = useChat();
const { setChangeGroupData } = useGroup();
const { t } = useTranslation();
const [show, setShow] = useState<boolean>(false);

    return ( <>
    <Button
    color="none"
    type="button"
    onClick={() =>
      Swal.fire({
        title: t("Do you want to change the group name?"),
        text: newGroupName,
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: t("Yes"),
        cancelButtonText: t("No"),
        customClass: {
          popup: "swal-mini-popup"
        }
      }).then((result) => {
        if (result.isConfirmed) {
          Swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Changed!',
            showConfirmButton: false,
            timer: 1500,
            customClass: {
              popup: "swal-mini-popup"
            }
          })
          setChangeGroupData({
            groupId: activeChat.groupId,
            groupName: newGroupName,
            members: actualMembers,
            description: "Descrição do grupo!",
          });
          setGroupName("");
        } else {
          setGroupName("");
        }
      })
    }
    >
      <i className="ri-edit-line"></i>
    </Button>

  <ModalGenerics
  setShow={setShow}
  show={show}
  title={"Do you want to change the group name?"}
  children={
    <div>
      <Button onClick={() => 
        {
          setChangeGroupData({
            groupId: activeChat.groupId,
            groupName: newGroupName,
            members: actualMembers,
            description: "Descrição do grupo!",
          });
          setGroupName("");
          setShow(!show);
          toast.success(t('Deleted!'), {
            position: "top-center",
            autoClose: 3000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
          });         
        }
      }>Confirmar</Button>
      <Button onClick={() => setGroupName("")}>Cancelar</Button>
    </div>
  }/>

  </>
  );
}

export default ChangeGroupNameButton;
