import { zeroLeft } from "./../helpers/zeroLeft";

export const today = () => {
  var today = new Date();
  var dd = zeroLeft(today.getDate(), 2);
  var m = zeroLeft(today.getMonth() + 1, 2);
  var yyyy = today.getFullYear();

  var hh = zeroLeft(today.getHours(), 2);
  var min = zeroLeft(today.getMinutes(), 2);

  return {
    date: `${yyyy}-${m}-${dd}`,
    time: `${hh}:${min}`,
  };
};

export const getAllTimeDay = () => {
  let times: string[] = [];

  for (var h = 0; h < 24; h++) {
    for (var m = 0; m < 4; m++) {
      times.push(`${zeroLeft(h, 2)}:${zeroLeft(m * 15, 2)}`);
    }
  }

  return times;
};
