import React, { createContext, useContext, useState } from "react";
import { useChat } from "./ChatContext";

//alias
import IChat from "../alias/IChat";
import IGroup from "../alias/IGroup";
import IContact from "./../alias/IContact";
import IMember from "./../alias/IMember";
import ChatType from "../enuns/ChatType";

interface IContactContextData {
  openContact(value: IContact): void;
  contacts: IContact[];
  setContacts(value: IContact[]): void;
  newFavorite: IChat | IGroup | IContact | undefined;
  setNewFavorite(favorited: IChat | IGroup | IContact | undefined): void;
  contactsSearch: string;
  setContactsSearch(value: string): void;
  contactsSearched: IContact[] | undefined;
  setContactsSearched(val: IContact[] | undefined): void;
  contactsOutsideUserContacts: IContact[];
  setContactsOutsideUserContacts(value: IContact[]): void;
  toggleAddingContacts: boolean;
  setToggleAddingContacts(value: boolean): void;
  toggleAddingMembers: boolean;
  setToggleAddingMembers(value: boolean): void;
  checkedContacts: IContact[];
  setCheckedContacts(value: IContact[]): void;
  removedContact: IContact | undefined;
  setRemovedContact(value: IContact | undefined) : void;
  checkedMembers: IContact[];
  setCheckedMembers(value: IContact[]): void;
  contactsOutsideGroup: IContact[];
  setContactsOutsideGroup(value: IContact[]): void;
  groupToInsertMember: IGroup | undefined;
  setGroupToInsertMember(value: IGroup | undefined): void;
}

export const ContactContext = createContext<IContactContextData>(
  {} as IContactContextData
);

export const ContactProvider: React.FC = ({ children }) => {
  const [contacts, setContacts] = useState<IContact[]>([]);
  const [contactsOutsideUserContacts, setContactsOutsideUserContacts] = useState<IContact[]>([]);
  const [contactsOutsideGroup, setContactsOutsideGroup] = useState<IContact[]>([]);
  const [removedContact, setRemovedContact] = useState<IContact | undefined>(undefined);
  const [checkedContacts, setCheckedContacts] = useState<IContact[]>([]);
  const [toggleAddingContacts, setToggleAddingContacts] = useState(false);
  const [checkedMembers, setCheckedMembers] = useState<IContact[]>([]);
  const [toggleAddingMembers, setToggleAddingMembers] = useState(false);
  const [groupToInsertMember, setGroupToInsertMember] = useState<IGroup | undefined>(undefined);
  const { activeChat, chats, setChats, setActiveChat, setShowChat } = useChat();
  const [newFavorite, setNewFavorite] = useState<
    IChat | IGroup | IContact | undefined
  >();
  const [contactsSearch, setContactsSearch] = useState<string>("");
  const [contactsSearched, setContactsSearched] = useState<
    IContact[] | undefined
  >();

  const openContact = (obj: IContact) => {
    if (activeChat?.jid === obj.jid) return;
    let find = false;

    setChats(
      chats.map((item) => {
        if (item.jid === obj.jid) {
          item.unRead = 0;
          item.messageLoaded = item.messages.length >= 20 ? false : true;
          setActiveChat(item);
          find = true;
        }
        return item;
      })
    );

    if (!find) {
      let newChat: IChat = {
        id: obj.id,
        jid: obj.jid,
        name: obj.name,
        email: obj.email,
        department: obj.department,
        profilePicture: obj.profilePicture,
        status: obj.status,
        messageLoaded: true,
        unRead: 1,
        chatType: ChatType.USER,
        inputMessage: "",
        isTyping: false,
        messages: [],
        favorite: obj.favorite,
        added: obj.added,
      };
      setShowChat(true);
      setActiveChat(newChat);
    }
  };

  return (
    <ContactContext.Provider
      value={{
        openContact,
        contacts,
        setContacts,
        newFavorite,
        setNewFavorite,
        contactsSearch,
        setContactsSearch,
        contactsSearched,
        setContactsSearched,
        contactsOutsideUserContacts,
        setContactsOutsideUserContacts,
        toggleAddingContacts,
        setToggleAddingContacts,
        checkedContacts,
        setCheckedContacts,
        removedContact,
        setRemovedContact,
        toggleAddingMembers,
        setToggleAddingMembers,
        checkedMembers,
        setCheckedMembers,
        contactsOutsideGroup,
        setContactsOutsideGroup,
        groupToInsertMember,
        setGroupToInsertMember
      }}
    >
      {children}
    </ContactContext.Provider>
  );
};

export function useContact() {
  return useContext(ContactContext);
}
