import React from "react";

import { Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";

interface IProps {
  show: boolean;
  setShow(value: boolean): void;
  title: string;
  description: string;
}

const ModalGenerics: React.FC<IProps> = ({
  children,
  show,
  setShow,
  title,
  description
}) => {
  return (
    <Modal backdrop="static" isOpen={show} centered>
      <ModalHeader tag="h5" className="modal-title font-size-16" toggle={() => setShow(!show)}>{title}</ModalHeader>
      <ModalBody className="p-4">{description}</ModalBody>
      <ModalFooter>{children}</ModalFooter>
    </Modal>
  );
};

export default ModalGenerics;
