import React, { useEffect } from "react";
import { SquadCommunicator as SquadService } from "./SquadCommunicatorService";
import { useGroup } from "../contexts/GroupContext";
import { useContact } from "../contexts/ContactContext";
import IContact from "../alias/IContact";
import IGroup from "../alias/IGroup";
import { Events as VoiceEvents } from "./voice/types";
import { useCall } from "../contexts/CallContext";
import ICall from "../alias/ICall";
import { Invitation, Inviter } from "sip.js";
import CallStatus from "../enuns/CallStatus";
//import { callbackify } from "util";

// import IContact from "../alias/IContact";
// Comentado para Evitar Warnings no console
// interface CreateGroupCallback {
//   (groupCreated: boolean): void;
// }

let squadService: SquadService | undefined;

const SquadSipCommunicator: React.FC = () => {
  const { contacts } = useContact();
  const {
    currentCalls,
    setCurrentCalls,
    callNumber,
    setCallNumber,
  } = useCall();
  const { groups } = useGroup();

  const init = () => {
    squadService = SquadService.getInstance({
      voiceCB: voiceCommuncatorSubscribe,
    });
  };
  const setNewCall = (call: ICall) => {
    const cc = new Map<string, ICall>(currentCalls);
    cc.set(call.callId, call);
    setCurrentCalls(cc);
  };
  const removeCall = (call: ICall) => {
    if (call.interval) clearTimeout(call.interval);
    const cc = new Map<string, ICall>(currentCalls);
    cc.delete(call.callId);
    setCurrentCalls(cc);
  };
  const onMakeCall = () => {
    if (callNumber?.number) {
      const constraints = {
        useAudio: callNumber?.useAudio,
        useVideo: callNumber?.useVideo,
      };
      squadService?.sip?.makeCall(callNumber?.number, constraints);
      setCallNumber(undefined);
    }
  };

  function voiceCommuncatorSubscribe(event: VoiceEvents, data: any) {
    let endCall;
    switch (event) {
      case VoiceEvents.CONNECTED:
        // squadService?.sip.sip.invite("2045");
        console.log("connected");
        break;
      case VoiceEvents.RECEIVED_CALL:
        const invitation = data as Invitation;
        const invitation_caller_id_number = invitation.remoteIdentity.uri.aor
          ?.split("@")[0]
          ?.replace("sip:", "")
          .replace("citrus-conference-authenticated-", "");
        const invitation_ctct_of_number =
          contacts.find((ctc) => ctc.number === invitation_caller_id_number) ||
          groups.find((grp) => grp.groupId === inviter_caller_id_number);
        let receivedCall: ICall;
        const acceptCall = (useVideo: boolean = false) => {
          squadService?.sip.updateConstraintsParams({ useVideo });
          invitation.accept({
            sessionDescriptionHandlerOptions: {
              constraints: squadService?.sip.getConstraints(),
            },
          });
          if (receivedCall) {
            setNewCall({
              ...receivedCall,
              callStatus: CallStatus.CONNECTING,
              sendingVideo: useVideo,
            });
          }
        };
        endCall = () => {
          squadService?.sip.sip.endCall(invitation);
        };
        receivedCall = {
          callId: invitation.id,
          callStatus: CallStatus.RINGING,
          callerNumber: invitation_caller_id_number,
          callerObject: invitation_ctct_of_number,
          callerId:
            (invitation_ctct_of_number as IContact)?.id ||
            (invitation_ctct_of_number as IGroup)?.groupId,
          callerName: invitation_ctct_of_number?.name,
          callerProfilePicture: invitation_ctct_of_number?.profilePicture,
          session: invitation,
          isGroup: !!(invitation_ctct_of_number as IGroup)?.groupId,
          accept: acceptCall,
          endCall,
          micMuted: false,
          videoMuted: false,
          muteMic: () => squadService?.sip.muteMic(invitation.id),
          unMuteMic: () => squadService?.sip.unMuteMic(invitation.id),
          disableCam: () => squadService?.sip.disableCam(invitation.id),
          enableCam: () => squadService?.sip.enableCam(invitation.id),
        };
        setNewCall(receivedCall);
        break;
      case VoiceEvents.MAKE_CALL:
        const inviter = data as Inviter;
        const localConstraints = inviter?.sessionDescriptionHandlerOptions
          ?.constraints as MediaStreamConstraints;
        const inviter_caller_id_number = inviter.remoteIdentity.uri.aor
          ?.split("@")[0]
          ?.replace("sip:", "")
          .replace("citrus-conference-authenticated-", "");
        const inviter_ctct_of_number =
          contacts.find((ctc) => ctc.number === inviter_caller_id_number) ||
          groups.find((grp) => grp.groupId === inviter_caller_id_number);

        endCall = () => {
          squadService?.sip.sip.endCall(inviter);
        };
        const sendCall: ICall = {
          callId: inviter.id,
          callStatus: CallStatus.CALLING,
          callerNumber: inviter_caller_id_number,
          callerObject: inviter_ctct_of_number,
          callerId:
            (inviter_ctct_of_number as IContact)?.id ||
            (inviter_ctct_of_number as IGroup)?.groupId,
          callerName: inviter_ctct_of_number?.name,
          callerProfilePicture: inviter_ctct_of_number?.profilePicture,
          session: inviter,
          isGroup: !!(inviter_ctct_of_number as IGroup)?.groupId,
          sendingVideo: localConstraints?.video !== false,
          endCall,
          muteMic: () => squadService?.sip.muteMic(inviter.id),
          unMuteMic: () => squadService?.sip.unMuteMic(inviter.id),
          disableCam: () => squadService?.sip.disableCam(inviter.id),
          enableCam: () => squadService?.sip.enableCam(inviter.id),
        };
        setNewCall(sendCall);
        console.log("make_call");
        break;
      case VoiceEvents.CALL_ON_GOING:
        const callOnGoing =
          currentCalls.get(data?.invitation?.id) ||
          currentCalls.get(data?.inviter?.id);
        if (callOnGoing) {
          const cc = { ...callOnGoing };
          cc.callStatus = CallStatus.ON_GOING;
          cc.receivingVideo = data.receivingVideo;
          cc.tagId = data.tagId;
          setNewCall(cc);
        }
        break;
      case VoiceEvents.CALL_HANGUP:
        const hangupCall = currentCalls.get(data.id);
        if (hangupCall) {
          removeCall(hangupCall);
        }
        break;
      default:
        console.log(event);
        break;
    }
  }

  const updateFunction = () => {
    squadService?.updateVoiceSubscribeFunction(voiceCommuncatorSubscribe);
  };

  useEffect(init, []);
  useEffect(updateFunction, [contacts, groups, currentCalls, callNumber]);
  useEffect(onMakeCall, [callNumber]);

  return <></>;
};

export default SquadSipCommunicator;
