import React, { CSSProperties, useState, useRef, useEffect } from "react";
import { Modal, ModalBody, Row, Col, Container, Button } from "reactstrap";
import { useCall } from "../contexts/CallContext";

const buttonStyles: CSSProperties = {
  width: "100%",
  marginBottom: "12px",
};

interface IProps {
  show: boolean;
  toggle(): void;
}

const DialPad: React.FC<IProps> = ({ show, toggle }) => {
  const [dialNumber, setDialNumber] = useState<string>("");
  const { setCallNumber, blindTransfer, setBlindTransfer } = useCall();
  const ref = useRef<HTMLInputElement>(null);

  const onChangeDialNumber = (number: string, dtmf = true) => {
    const value = number.replace(/\D/g, "");
    setDialNumber(value);
  };

  const onPaste = (event: React.ClipboardEvent<HTMLInputElement>) => {
    event.preventDefault();
    const clipboardData = event.clipboardData || (window as any).clipboardData;
    const pastedData = clipboardData.getData("Text");
    setDialNumber(pastedData.replace(/[^0-9]/g, ""));
  };

  function makeCall() {
    if (blindTransfer?.callId) {
      setBlindTransfer({ ...blindTransfer, number: dialNumber });
    } else {
      setCallNumber({
        number: dialNumber,
        useVideo: false,
        useAudio: true,
      });
    }
    setDialNumber("");
    toggle();
  }

  function init() {
    setTimeout(() => {
      ref.current?.focus();
    }, 100);
  }

  useEffect(init, [show]);

  return (
    <Modal style={{ width: "350px" }} isOpen={show} centered toggle={toggle}>
      <ModalBody>
        <div className="d-flex justify-content-center mt-3">
          <input
            style={{
              border: "none",
              borderBottom: "1px solid rgba(0,0,0,0.2)",
              borderRadius: "0px",
              fontSize: "17pt",
            }}
            ref={ref}
            className="mx-5 form-control"
            autoFocus={true}
            value={dialNumber}
            onPaste={onPaste}
            onKeyPress={(e) => {
              if (e.key === "Enter" && dialNumber.length > 0) {
                makeCall();
              }
            }}
            onChange={(event) => onChangeDialNumber(event.target.value)}
          />
        </div>

        <Container className="mt-4 mb-2">
          <Row>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "1");
                }}
              >
                <span>1</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "2");
                }}
              >
                <span>2</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "3");
                }}
              >
                <span>3</span>
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "4");
                }}
              >
                <span>4</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "5");
                }}
              >
                <span>5</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "6");
                }}
              >
                <span>6</span>
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "7");
                }}
              >
                <span>7</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "8");
                }}
              >
                <span>8</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "9");
                }}
              >
                <span>9</span>
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "*");
                }}
              >
                <span>*</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "0");
                }}
              >
                <span>0</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                onKeyPress={(e) => {
                  if (e.key === "Enter" && dialNumber.length > 0) {
                    makeCall();
                  }
                }}
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "#");
                }}
              >
                <span>#</span>
              </Button>
            </Col>
          </Row>
        </Container>

        <Row className="mb-4">
          <Col className="d-flex justify-content-center align-items-center">
            <Button
              color="danger"
              style={{ width: "70px", fontSize: "15pt" }}
              className="d-flex justify-content-center align-items-center"
              onClick={() => {
                setDialNumber(dialNumber.substring(0, dialNumber.length - 1));
              }}
            >
              <i className="ri-delete-back-2-line"></i>
            </Button>
          </Col>
          <Col className="d-flex justify-content-center align-items-center">
            <Button
              color="primary"
              style={{ width: "70px", fontSize: "15pt" }}
              className="d-flex justify-content-center align-items-center"
              onClick={makeCall}
            >
              <i className="ri-phone-line"></i>
            </Button>
          </Col>
        </Row>
      </ModalBody>
    </Modal>
  );
};

export default DialPad;
