import React, { useEffect, useState } from "react";
import { Button, Card, Media, Badge } from "reactstrap";
import SimpleBar from "simplebar-react";
import CustomCollapse from "./CustomCollapse";
import { useTranslation } from "react-i18next";
import { useChat } from "../contexts/ChatContext";
import AttachedFilesTab from "./AttachedFilesTab";
import ReactImageFallback from "react-image-fallback";
import Swal from "sweetalert2";
import defaultAvatar from "../assets/images/default.png";
import { onImgError } from "../helpers/ImageUtils";
import { useGroup } from "../contexts/GroupContext";
import { useContact } from "../contexts/ContactContext";
import { getUserStatusName } from "../helpers/StatusUtils";
import { useAuth } from "../contexts/AuthContext";
import { useModal } from "./../contexts/ModalContext";
import ModalAddMember from "./ModalAddMember";
import { Input } from '@material-ui/core';
import { string } from "yup";

interface IPros {
  show: boolean;
  setShow(value: boolean): void;
}

const ChatProfileSidebar: React.FC<IPros> = ({ show, setShow }) => {
  const { user } = useAuth();
  const { activeChat, chats, setChats, setActiveChat } = useChat();
  const [ targetStorage, setTargetStorage ] = useState('');
  const { groups, 
    setGroups, 
    setMemberQuit, 
    setGroupIdOfLeaveRequest, 
    setRemovedMember, 
    setGroupToRemoveContact, 
    setToggleMemberRole,
    setChangeGroupData
   } = useGroup();
  const { contacts, setContacts } = useContact();
  const { setShowModalAddMember } = useModal();
  const [isOpen1, setIsOpen1] = useState(true);
  const [isOpen2, setIsOpen2] = useState(false);
  const { t } = useTranslation();
  const toggleCollapse1 = () => {
    setIsOpen1(!isOpen1);
    setIsOpen2(false);
  };
  const toggleCollapse2 = () => {
    setIsOpen1(false);
    setIsOpen2(!isOpen2);
  };
  const closeUserSidebar = () => {
    setShow(false);
  };
  const getAttachedFiles = () => {
    if (show && activeChat.attachedFileList === undefined) {
      setActiveChat({ ...activeChat, attachedFileList: [] });
    } else if (!show) {
      setActiveChat({ ...activeChat, attachedFileList: undefined });
    }
  };
  useEffect(getAttachedFiles, [show]);
  const closeUserSidebarOnActiveChatChange = () => {
    if (show && activeChat.attachedFileList === undefined) closeUserSidebar();
  };
  useEffect(closeUserSidebarOnActiveChatChange, [activeChat]);
  const actualMember = activeChat.members?.find((member) => member.userId === user.id);
  const isAdmin = actualMember?.role === "admin";
  //const groupAdmins = activeChat.members?.filter((member) => member.role === "admin");
  const actualMembers = activeChat.members?.map((mb) => {
    return mb.userId;
  });
  return (
    <React.Fragment>
      <div
        style={{ display: show ? "block" : "none" }}
        className="user-profile-sidebar "
      > 
        <div className="d-flex">
          {activeChat.groupId && 
          <div className="px-3 px-lg-4 pt-3 pt-lg-4" style={{marginLeft: '4.2vh'}}>
              <div className="user-chat-nav text-right">
                <Button
                  color="none"
                  type="button"
                  onClick={() => setShowModalAddMember(true)}
                >
                  ADD
                </Button>
              </div>
            </div>
          }
        

          <ModalAddMember/>

          {activeChat.groupId && 
            <div className="px-3 px-lg-4 pt-3 pt-lg-4">
            <div className="user-chat-nav text-right">
              <Button
                color="none"
                type="button"
                onClick={() => Swal.fire({
                  title:
                    t("Leave the group?"),
                  text: "",
                  icon: "warning",
                  showCancelButton: true,
                  confirmButtonColor: "#4e5e96",
                  cancelButtonColor: "#d33",
                  cancelButtonText: t("No"),
                  confirmButtonText: t("Yes"),
                }).then((result) => {
                  if (result.isConfirmed) {
                    Swal.fire(
                      "You left the group",
                      "",
                      "success",
                    )
                    activeChat.groupId && setGroupIdOfLeaveRequest(activeChat.groupId);
                    setMemberQuit(actualMember);
                  }
                })
              }
                className="nav-btn"
                id="user-profile-hide"
              >
                <i className="ri-door-lock-box-line"></i>
              </Button>
            </div>
          </div>
          }
          
          {activeChat.groupId &&
            <div className="px-3 px-lg-4 pt-3 pt-lg-4">
              <div className="user-chat-nav text-right">
                <Button
                  color="none"
                  type="button"
                  onClick={closeUserSidebar}
                  className="nav-btn"
                  id="user-profile-hide"
                >
                  <i className="ri-close-line"></i>
                </Button>
              </div>
            </div>
          }
        
        </div>

        <div className="text-center p-4 border-bottom">
          <div className="mb-4 d-flex justify-content-center">
            {!activeChat.profilePicture ? (
              <div className="avatar-lg">
                <span className="avatar-title rounded-circle bg-soft-primary text-primary font-size-24">
                  {activeChat.name.trim().charAt(0)}
                </span>
              </div>
            ) : (
              <ReactImageFallback
                src={activeChat.profilePicture}
                onError={() => {
                  if (activeChat.jid.includes("@conference."))
                    onImgError(
                      chats,
                      groups,
                      contacts,
                      setChats,
                      setGroups,
                      setContacts,
                      activeChat
                    );
                }}
                fallbackImage={defaultAvatar}
                className="rounded-circle avatar-lg img-thumbnail"
                alt="chatvia"
              />
            )}
          </div>
          {activeChat.groupId ? 
            <Input
            placeholder={activeChat.name}
            disableUnderline
            fullWidth={true}
            onChange={(e) => 
              setTargetStorage(e.target.value)
            }
            style={{
              marginLeft: '37%'
            }}
            />
            :
            <div>{activeChat.name}</div>
          }
            { activeChat.groupId &&
            
            <Button
              color="none"
              type="button"
              onClick={ () =>
                Swal.fire({
                  title: 'Do you want to change the group name?',
                  text: "To: " + targetStorage + "?",
                  icon: 'warning',
                  showCancelButton: true,
                  confirmButtonColor: '#3085d6',
                  cancelButtonColor: '#d33',
                  confirmButtonText: 'Yes!'
                }).then((result) => {
                  if (result.isConfirmed) {
                    Swal.fire(
                      'Changed!',
                      '',
                      'success'
                    );
                    setTargetStorage('');
                  }else{
                    setTargetStorage('');
                  }
                })
              }
              >
                <i className="ri-edit-line"></i>
            </Button>
            }
            
          {activeChat.status && (
            <p className="text-muted text-truncate mb-1">
              {(() => {
                switch (activeChat.status) {
                  case "online":
                    return (
                      <>
                        <i className="ri-record-circle-fill font-size-10 text-success mr-1"></i>
                      </>
                    );

                  case "away":
                    return (
                      <>
                        <i className="ri-record-circle-fill font-size-10 text-warning mr-1"></i>
                      </>
                    );

                  case "dnd":
                    return (
                      <>
                        <i className="ri-record-circle-fill font-size-10 text-danger mr-1"></i>
                      </>
                    );

                  default:
                    return (
                      <>
                        <i className="ri-record-circle-fill font-size-10 text-secondary mr-1"></i>
                      </>
                    );
                }
              })()}
              {getUserStatusName(activeChat.status)}
            </p>
          )}
        </div>
        <SimpleBar
          style={{ maxHeight: "100%" }}
          className="p-4 user-profile-desc"
        >
          <div id="profile-user-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              <CustomCollapse
                title="About"
                iconClass="ri-user-2-line"
                isOpen={isOpen1}
                toggleCollapse={toggleCollapse1}
              >
                <div>
                  <p className="text-muted mb-1">{activeChat.groupId ? "Group name:" : t("Name:")}</p>
                  <h5 className="font-size-14">{activeChat.name}</h5>
                </div>

                <div className="text-muted">
                  <p className="mb-4">
                    {activeChat.userDescription === undefined && !activeChat.groupId
                      ? t("Hey! I'm on Squad!")
                      : activeChat.userDescription}
                  </p>
                </div>
                {activeChat.email && (
                  <div className="mt-4">
                    <p className="text-muted mb-1">{t("Email")}</p>
                    <h5 className="font-size-14">{activeChat.email}</h5>
                  </div>
                )}
                {activeChat.department && (
                  <div className="mt-4">
                    <p className="text-muted mb-1">{t("Department")}</p>
                    <h5 className="font-size-14 mb-0">
                      {activeChat.department}
                    </h5>
                  </div>
                )}
              </CustomCollapse>
            </Card>

            {activeChat.jid.includes("@conference.") && (
              <Card className="mb-1 shadow-none border">
                <CustomCollapse
                  title="Members"
                  iconClass="ri-group-line"
                  isOpen={isOpen2}
                  toggleCollapse={toggleCollapse2}
                >
                  {activeChat.members?.map((member) => (
                    <Card key={member.userId} className="p-2 mb-2">
                      <Media className="align-items-center">
                        <ReactImageFallback
                          src={member.profilePicture}
                          className="rounded-circle avatar-xs mr-2"
                          alt="chatvia"
                          fallbackImage={defaultAvatar}
                        />
                        <Media body>
                          <div className="text-left">
                            <h5 className="font-size-14 mb-1">
                              {member.name}
                              {(isAdmin && user.id !== member.userId) && (
                                <span
                                  className="float-right ml-2"
                                  style={{ cursor: "pointer" }}
                                  onClick={() =>
                                    Swal.fire({
                                      title:
                                        t("Remove") + " " + member.name + "?",
                                      text: "",
                                      icon: "warning",
                                      showCancelButton: true,
                                      confirmButtonColor: "#4e5e96",
                                      cancelButtonColor: "#d33",
                                      cancelButtonText: t("No"),
                                      confirmButtonText: t("Yes"),
                                    }).then((result) => {
                                      if (result.isConfirmed) {
                                        Swal.fire(
                                          member.name +
                                            " " +
                                            t("has been removed!"),
                                          "",
                                          "success"
                                        )
                                        setRemovedMember(member);
                                        setGroupToRemoveContact(activeChat.groupId)
                                      }
                                    })
                                  }
                                >
                                  <i className="ri-close-fill"></i>
                                </span>
                              )}
                              {member.role === "admin" ? (
                                <Badge
                                  color="danger"
                                  className="badge-soft-danger float-right"
                                  onClick={() =>
                                    isAdmin &&
                                    Swal.fire({
                                      title:
                                        t("Turn") +
                                        " " +
                                        member.name +
                                        " " +
                                        t("a member?"),
                                      text: "",
                                      icon: "warning",
                                      showCancelButton: true,
                                      confirmButtonColor: "#4e5e96",
                                      cancelButtonColor: "#d33",
                                      cancelButtonText: t("No"),
                                      confirmButtonText: t("Yes"),
                                      padding: "5%",
                                    }).then((result) => {
                                      if (result.isConfirmed) {
                                        Swal.fire(
                                          member.name +
                                            " " +
                                            t("is now a member!"),
                                          "",
                                          "success"
                                        );
                                      }
                                      setToggleMemberRole({
                                        mbId: member.userId,
                                        gpId: activeChat.groupId ? activeChat.groupId : '',
                                        mbRole: member.role,
                                        mb: member
                                      });
                                    })
                                  }
                                  style={{ cursor: "pointer" }}
                                >
                                  {t("Admin")}
                                </Badge>
                              ) : (
                                <Badge
                                  color="success"
                                  className="badge-soft-success float-right"
                                  style={{ cursor: "pointer" }}
                                  onClick={() =>
                                    isAdmin &&
                                    Swal.fire({
                                      title:
                                        t("Turn") +
                                        " " +
                                        member.name +
                                        " " +
                                        t("an administrator?"),
                                      text: "",
                                      icon: "warning",
                                      showCancelButton: true,
                                      confirmButtonColor: "#4e5e96",
                                      cancelButtonColor: "#d33",
                                      cancelButtonText: t("No"),
                                      confirmButtonText: t("Yes"),
                                    }).then((result) => {
                                      if (result.isConfirmed) {
                                        Swal.fire(
                                          member.name +
                                            " " +
                                            t("is now an administrator!"),
                                          "",
                                          "success"
                                        );
                                        setToggleMemberRole({
                                          mbId: member.userId,
                                          gpId: activeChat.groupId ? activeChat.groupId : '',
                                          mbRole: member.role,
                                          mb: member
                                        });
                                      }
                                    })
                                  }
                                >
                                  {t("Member")}
                                </Badge>
                              )}
                            </h5>
                          </div>
                        </Media>
                      </Media>
                    </Card>
                  ))}
                </CustomCollapse>
              </Card>
            )}
            <AttachedFilesTab />
          </div>
        </SimpleBar>
      </div>
    </React.Fragment>
  );
};

export default ChatProfileSidebar;
