import React, { useState } from "react";
import {
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  UncontrolledDropdown,
  Card,
  Media,
} from "reactstrap";
import { Link } from "react-router-dom";

//i18n
import { useTranslation } from "react-i18next";

//lightbox
import Lightbox from "react-image-lightbox";
import "react-image-lightbox/style.css";

import avatar4 from "../assets/images/users/avatar-4.jpg";

import IAttachedFiles from "./../alias/IAttachedFiles";

interface IMediaTabFiles{
  list: IAttachedFiles[]; 
}

const MediaTabFiles: React.FC<IMediaTabFiles> = ({list}) => {
  const [isOpen, setIsOpen] = useState(false);
  const [currentImage, setCurrentImage] = useState("null");

  const [files] = useState<IAttachedFiles[]>([
    {
      name: "Package.rar",
      // size: "12.5 MB",
      thumbnail: avatar4,
      isAudioMessage: false,
      isImageMessage: true,
      isVideoMessage: false,
      isFileMessage: false,
    },
    {
      name: "Image-1.jpeg",
      // size: "4.2 MB",
      thumbnail: avatar4,
      isAudioMessage: false,
      isImageMessage: true,
      isVideoMessage: false,
      isFileMessage: false,
    },
    {
      name: "Song-1.jpeg",
      // size: "3.1 MB",
      thumbnail: avatar4,
      isAudioMessage: false,
      isImageMessage: true,
      isVideoMessage: false,
      isFileMessage: false,
    },
    {
      name: "Photo.png",
      // size: "1.7 MB",
      thumbnail: avatar4,
      isAudioMessage: false,
      isImageMessage: true,
      isVideoMessage: false,
      isFileMessage: false,
    },
    {
      name: "Landing-A.zip",
      // size: "6.7 MB",
      thumbnail: avatar4,
      isAudioMessage: false,
      isImageMessage: true,
      isVideoMessage: false,
      isFileMessage: false,
    },
    {
      name: "Giffy.gif",
      // size: "2.7 MB",
      thumbnail: avatar4,
      isAudioMessage: false,
      isImageMessage: true,
      isVideoMessage: false,
      isFileMessage: false,
    },
  ]);

  const { t } = useTranslation();

  const toggleLightbox: any = (currentImage: any) => {
    setIsOpen(!isOpen);
    setCurrentImage(currentImage);
  };

  return (
    <React.Fragment>
      <Card className="p-2 border mb-2 d-flex flex-row flex-wrap">
        {files.map((item, index) =>
          item.isImageMessage || item.isVideoMessage ? (
            <Media
              key={index}
              header="true"
              className="align-content-end bd-highlight"
            >
              <div className="ml-1">
                <Link
                  to="#"
                  onClick={() => toggleLightbox(item.thumbnail)}
                  className="popup-img d-inline-block m-1"
                  title={item.name}
                >
                  <img
                    src={item.thumbnail}
                    alt={item.name}
                    className="rounded border"
                    style={{ width: 140, height: 140 }}
                  />
                </Link>
                <Media>
                  <div className="ml-1">
                    <ul className="list-inline mb-0 font-size-18">
                      <li className="list-inline-item">
                        <Link to="#" className="text-muted px-1">
                          <i className="ri-download-2-line"></i>
                        </Link>
                      </li>
                      <UncontrolledDropdown className="list-inline-item">
                        <DropdownToggle className="text-muted px-1" tag="a">
                          <i className="ri-more-fill"></i>
                        </DropdownToggle>
                        <DropdownMenu left="true">
                          <DropdownItem>{t("Favorite message")}</DropdownItem>
                          <DropdownItem>{t("Forward message")}</DropdownItem>
                          <DropdownItem divider />
                          <DropdownItem>{t("Delete")}</DropdownItem>
                        </DropdownMenu>
                      </UncontrolledDropdown>
                    </ul>
                  </div>
                </Media>
              </div>
            </Media>
          ) : null
        )}
      </Card>
      {isOpen && (
        <Lightbox
          mainSrc={currentImage}
          onCloseRequest={toggleLightbox}
          imageTitle="Image File"
        />
      )}
    </React.Fragment>
  );
};

export default MediaTabFiles;
