import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  Input,
  ModalFooter,
} from "reactstrap";

import { useModal } from "../contexts/ModalContext";
import { useTranslation } from "react-i18next";
import SimpleBar from "simplebar-react";
import IContact from "../alias/IContact";
import { useContact } from "./../contexts/ContactContext";
import SelectContactGroup from "./SelectContactGroup";

const ModalManageGroup: React.FC = () => {
  const [search, setSearch] = useState("");

  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);

  const { t } = useTranslation();
  const {
    setToggleAddingContacts,
    /* toggleAddingContacts, */ setCheckedContacts,
  } = useContact();

  const { showModalManageGroup, setShowModalManageGroup } = useModal();

  const toggle = () => {
    if (showModalManageGroup) {
      setShowModalManageGroup(!showModalManageGroup);
    }
  };

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      let selected = selectedContact;
      selected.push(contact);
      setSelectedContact(selected);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.jid === contact.jid) {
            return false;
          }
          return true;
        })
      );
    }
  };

  //internacionalização

  return (
    <Modal
      backdrop="static"
      isOpen={!!showModalManageGroup}
      centered
      toggle={toggle}
    >
      <ModalHeader toggle={toggle}>{t("Add Contacts")}</ModalHeader>
      <ModalBody>
        <CardBody className="p-2 border">
          <div className="pt-2 pb-3 border-bottom">
            <Input
              type="text"
              className="form-control mb-3"
              id="addgroupname-input"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder={t("Search users..")}
            />
            <hr />
            <SimpleBar style={{ height: "500px" }}>
              <SelectContactGroup
                handleCheckContact={handleCheckContact}
                search={search}
              />
            </SimpleBar>
          </div>
          <ModalFooter className="border-0">
            <Button
              color="default"
              onClick={() => {
                setCheckedContacts([]);
                toggle();
              }}
            >
              {t("Close")}
            </Button>
            <Button
              color="primary"
              onClick={() => {
                toggle();
                setToggleAddingContacts(true);
              }}
            >
              {t("Add Contacts")}
            </Button>
          </ModalFooter>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalManageGroup;
