import React, { useState } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import Routes from "./routes";

import Dashboard from "./pages/Dashboard";
import IncomingCall from "./components/IncomingCall";
import { useLoading } from "./contexts/LoadingContext";
import LoadingPage from "./components/LoadingPage";
import { useChat } from "./contexts/ChatContext";
import Favicon from "react-favicon";
import ReadStatus from "./enuns/ReadStatus";

function App() {
  const { isLoadingApp } = useLoading();
  const {unreadSum, chats, setUnreadSum} = useChat();
  
  setTimeout(() => {
    console.log(
      chats.forEach((cht) => cht.messages.filter(
        (msg) => msg.readStatus === ReadStatus.UNREAD
        ).length
      )
    )
    setUnreadSum((chats.filter((cht) => cht.unRead > 0).length));
  }, 5000)
  
  return (
    <Router>
      {isLoadingApp ? (
        <LoadingPage />
      ) : (
        <>
          {unreadSum === 0 || unreadSum < 0 ? 
            (<Favicon url="./favicon.ico"/>) : 
            (<Favicon alertCount={unreadSum} url="./favicon.ico"/>) }
            {console.log('unreadSum', unreadSum)}
          <Dashboard>
            <Routes />
          </Dashboard>
          <IncomingCall />
        </>
      )}
    </Router>
  );
}

export default App;
