import React, { useEffect, useState, useRef } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
} from "reactstrap";

//simple bar
import SimpleBar from "simplebar-react";

//components
import SelectContact from "./SelectContact";

import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import { useGroup } from "../contexts/GroupContext";
import { useContact } from "../contexts/ContactContext";

interface IProps {
  show: boolean;
  toggle(): void;
}

export const ModalNewGroup: React.FC<IProps> = ({ show, toggle }) => {
  const { t } = useTranslation();
  const { contacts } = useContact();

  const [isOpenAlert, setIsOpenAlert] = useState(false);
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");
  const [groupName, setGroupName] = useState("");
  const [groupDescription, setGroupDescription] = useState("");
  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);
  const { setGroupToCreate } = useGroup();

  const handleCreateGroup = () => {
    console.log(JSON.stringify(selectedContact));
    setGroupToCreate({
      contacts: selectedContact,
      description: groupDescription,
      name: groupName,
    });

    setIsOpenAlert(true);
    setMessage("Criando grupo...");

    setTimeout(() => {
      setIsOpenAlert(false);
      setMessage("");
    }, 2000);

    setSearch("");
  };

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      let selected = selectedContact;
      selected.push(contact);
      setSelectedContact(selected);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };

  const inputGroupName = useRef<HTMLInputElement>(null);

  useEffect(() => {
    setTimeout(() => inputGroupName.current?.focus(), 500);
  }, [show]);

  return (
    <Modal isOpen={show} centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        {t("Create group")}
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <FormGroup className="mb-4">
            <Label htmlFor="addgroupname-input">{t("Group Name")}</Label>
            <input
              ref={inputGroupName}
              type="text"
              className="form-control"
              id="addgroupname-input"
              value={groupName}
              onChange={(e) => setGroupName(e.target.value)}
              placeholder={t("Group Name")}
            />
          </FormGroup>
          <FormGroup className="mb-4">
            <Label>{t("Group Members")}</Label>
            <Input
              type="text"
              className="form-control mb-2"
              id="addgroupname-input"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder={t("Search users..")}
            />
            <div className="border">
              <SimpleBar style={{ height: "250px" }}>
                <div id="addContacts">
                  <SelectContact
                    contacts={contacts}
                    handleCheckContact={handleCheckContact}
                    search={search}
                  />
                </div>
              </SimpleBar>
            </div>
          </FormGroup>
          <FormGroup>
            <Label htmlFor="addgroupdescription-input">
              {t("Description")}
            </Label>
            <textarea
              className="form-control"
              id="addgroupdescription-input"
              value={groupDescription}
              onChange={(e) => setGroupDescription(e.target.value)}
              rows={3}
              placeholder={t("Description")}
            ></textarea>
          </FormGroup>
        </Form>
        <Alert isOpen={isOpenAlert} color="danger">
          {message}
        </Alert>
      </ModalBody>
      <ModalFooter>
        <Button type="button" color="link" onClick={toggle}>
          {t("Close")}
        </Button>
        <Button type="button" color="primary" onClick={handleCreateGroup}>
          {t("Create group")}
        </Button>
      </ModalFooter>
    </Modal>
  );
};
