import React from 'react';
import IMessage from '../alias/IMessage';
import { useContact } from '../contexts/ContactContext';

interface IGroupNotification{
    msg: IMessage;
}

const GroupNotification : React.FC<IGroupNotification> = ({ msg }) => {
    let eventSender = msg.userName;
    const { contacts, contactsOutsideUserContacts } = useContact();

    switch(msg.eventId){
        case 2:
        let userThatIsBeingAdded: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingAdded = ctc.name;
            };
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingAdded = ctc.name;
            };
        })
        return(
            <div className="card">
                <div className="card-body" style={{color: "green"}}>
                {userThatIsBeingAdded || msg.eventBody} has been added by {eventSender}!
                </div>
            </div>
        );
        case 6:
        let userThatIsBeingRemoved: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingRemoved = ctc.name;
            };
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsBeingRemoved = ctc.name;
            };
        })
        return(
            <div className="card">
                <div className="card-body" style={{color: "red"}}>
                {userThatIsBeingRemoved || msg.eventBody} has been removed by {eventSender}!
                </div>
            </div>
        );
        default:
            return <></>    
    }
}

export default GroupNotification;
