import userEvent from "@testing-library/user-event";
import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  ModalFooter,
  Input
} from "reactstrap";

import { useModal } from "./../contexts/ModalContext";
import { useAuth } from './../contexts/AuthContext';
const ModalEditInfo: React.FC = () => {
  const {
    showModalEditInfo,
    setShowModalEditInfo
  } = useModal();

  const { setChangedUserData } = useAuth();
  const [ nome, setNome ] = useState("");
  const [ email, setEmail ] = useState("");

  const toggle = () => setShowModalEditInfo(!showModalEditInfo);

  return (
    <Modal
      backdrop="static"
      isOpen={showModalEditInfo}
      centered
      toggle={toggle}
    >
      <ModalHeader toggle={toggle}>Edit personal info</ModalHeader>
      <ModalBody>
        <CardBody>
          <form>
          <div className="mt-0">Edit name</div>
          <Input placeholder="Nome" onChange={(e) => setNome(e.target.value)}></Input>  
          <div className="mt-4">Edit e-mail</div>
          <Input placeholder="E-mail" onChange={(e) => setEmail(e.target.value)}></Input>  

          <ModalFooter className="border-0">
            <Button type="submit" color="default" onClick={toggle}>
              Close
            </Button>
            <Button type="submit" color="primary" 
            onClick={() => setChangedUserData({name: nome, email: email})}>
              Confirm
            </Button>
          </ModalFooter>
          </form>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalEditInfo;
