import React, { useState } from "react";
import {
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  Card,
  Media,
  UncontrolledDropdown,
} from "reactstrap";
import { Link } from "react-router-dom";

import IAttachedFiles from "../alias/IAttachedFiles";

import { useTranslation } from "react-i18next";

const DocumentTabFiles: React.FC = () => {
  const [files] = useState<IAttachedFiles[]>([
    {
      name: "Package.rar",
      // size: "12.5 MB",
      thumbnail: "ri-file-text-fill",
      isFileMessage: true,
      isVideoMessage: false,
      isAudioMessage: false,
      isImageMessage: false,
    },
    {
      name: "Image-1.jpeg",
      // size: "4.2 MB",
      thumbnail: "ri-image-fill",
      isFileMessage: false,
      isVideoMessage: false,
      isAudioMessage: false,
      isImageMessage: true,
    },
    {
      name: "Song-1-.mp3",
      // size: "3.1 MB",
      thumbnail: "ri-music-fill",
      isFileMessage: false,
      isVideoMessage: false,
      isAudioMessage: true,
      isImageMessage: false,
    },
    {
      name: "Photo.png",
      // size: "1.7 MB",
      thumbnail: "ri-image-text-fill",
      isFileMessage: false,
      isVideoMessage: false,
      isAudioMessage: false,
      isImageMessage: true,
    },
    {
      name: "Landing-A.zip",
      // size: "6.7 MB",
      thumbnail: "ri-file-text-fill",
      isFileMessage: true,
      isVideoMessage: false,
      isAudioMessage: false,
      isImageMessage: false,
    },
    {
      name: "Giffy.gif",
      // size: "2.7 MB",
      thumbnail: "ri-image-text-fill",
      isFileMessage: false,
      isVideoMessage: false,
      isAudioMessage: false,
      isImageMessage: true,
    },
  ]);

  const { t } = useTranslation();

  return (
    <React.Fragment>
      {files.map((item, key) =>
        item.name.includes(".rar" || ".zip") ? (
          <Card key={key} className="p-2 border mb-2">
            <Media className="align-items-center">
              <div className="avatar-sm mr-3">
                <div className="avatar-title bg-soft-primary text-primary rounded font-size-20">
                  <i className={item.thumbnail}></i>
                </div>
              </div>
              <Media body>
                <div className="text-left">
                  <h5 className="font-size-14 mb-1">{item.name}</h5>
                  {/* <p className="text-muted font-size-13 mb-0">{item.size}</p> */}
                </div>
              </Media>
              <div className="ml-4">
                <ul className="list-inline mb-0 font-size-18">
                  <li className="list-inline-item">
                    <Link to="#" className="text-muted px-1">
                      <i className="ri-download-2-line"></i>
                    </Link>
                  </li>
                  <UncontrolledDropdown className="list-inline-item">
                    <DropdownToggle className="text-muted px-1" tag="a">
                      <i className="ri-more-fill"></i>
                    </DropdownToggle>
                    <DropdownMenu right>
                      <DropdownItem>{t("Action")}</DropdownItem>
                      <DropdownItem>{t("Another Action")}</DropdownItem>
                      <DropdownItem divider />
                      <DropdownItem>{t("Delete")}</DropdownItem>
                    </DropdownMenu>
                  </UncontrolledDropdown>
                </ul>
              </div>
            </Media>
          </Card>
        ) : null
      )}
    </React.Fragment>
  );
};

export default DocumentTabFiles;
