import React from 'react';
import { Button } from 'reactstrap';
import 'react-toastify/dist/ReactToastify.css';
import useCopy from "@react-hook/copy";
import { useTranslation } from 'react-i18next';
import Swal from 'sweetalert2';
import { toast } from 'react-toastify';

export interface ICopyParticipantLinkButtonInActiveCall{
    participantLink: string,
    buttonIcon: string
}

const CopyParticipantLinkButtonInActiveCall : React.FC<ICopyParticipantLinkButtonInActiveCall> = ({participantLink, buttonIcon}) => {
    const { copy } = useCopy(participantLink);
    const { t } = useTranslation();
    return(
        <>
            <Button
            type="button"
            color="none"
            onClick={() => {
                copy();
                toast.success('Copied to clipboard a participant invite!', {
                    position: "top-center",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            }}
            className="nav-btn user-profile-show"
            style={{
                color: "black",
                cursor: "pointer",
                marginLeft: "5px",
                fontSize: "18px",
                padding: "5px",
            }}
            >
                <i 
                style={{
                    color: "black",
                    backgroundColor: "white",
                    borderRadius: 3,
                }}
                className={buttonIcon}/>
            </Button>
        </>
    )
}

export default CopyParticipantLinkButtonInActiveCall;
