import { red } from "@material-ui/core/colors";
import React, {useState} from "react";
import Dropzone from "react-dropzone";
import { useAuth } from "../contexts/AuthContext";
import { useChat } from "../contexts/ChatContext";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import './../assets/css/dragndrop.css'

interface IProps{
    addFile(value: any): any;
    files: any;
}

const ImageUpload: React.FC<IProps> = ({ addFile, files }) => {
    const { chats, setChats, activeChat } = useChat();
    const { user } = useAuth();

    const onAddMessage = (file: File) => {
        setChats(
        chats.map((item) => {
            if (activeChat)
            if (item.id === activeChat?.id) {
                item.messages.push({
                id: `${item.messages.length + 1}`,
                userName: user.name,
                message: "Carregando...",
                time: "",
                isImageMessage: false,
                isFileMessage: false,
                isVideoMessage: false,
                isAudioMessage: false,
                fileUrl: "",
                sendFile: file,
                toUser: activeChat.id,
                fromUser: user.id,
                deliverStatus: DeliverStatus.SENDING,
                readStatus: ReadStatus.UNREAD,
                deliverTimestamp: "",
                readTimestamp: "",
                content: "metadata",
                contentType: "type",
                isReplyMessage: false,
                isForwardMessage: false,
                });
            }
            return item;
        })
        );
    };
    const [warningMsg, setWarningMsg] = useState("");
    const onDrop = (accepted: any, rejected: any) => {
        if (Object.keys(rejected).length !== 0) {
        const message = "Please submit valid file type";
        setWarningMsg(message);
        } else {
            addFile(accepted);
            setWarningMsg("");
            var blobPromise = new Promise((resolve, reject) => {
                const reader = new window.FileReader();
                reader.readAsDataURL(accepted[0]);
                reader.onloadend = () => {
                const base64data = reader.result;
                resolve(base64data);
                };
            });
            /* blobPromise.then(value => {
                console.log(value);
            });  */

            if (files && files.size > 15728640) {
            alert(
                "Tamanho de arquivo " +
                files.name +
                " nÃ£o suportado. Por favor, envie um arquivo menor do que 15mb"
            );
            return;
            }else{
                onAddMessage(files);
            }
        }
    };

        return (
        <div className="selected">
            <p>{warningMsg}</p>

            <Dropzone
            multiple={false}
            noClick
            /* accept="image/*" */
            onDrop={(accepted, rejected) => onDrop(accepted, rejected)}
            >
            {({getRootProps, getInputProps}) => (
                <div {...getRootProps()}>
                <input {...getInputProps()} />
                img
                </div>
            )}
            </Dropzone>
        </div>
        );
    }

export default ImageUpload;
