import React from 'react';
import { useTranslation } from 'react-i18next';
import { Card } from 'reactstrap';
import CustomCollapse from "./CustomCollapse";
interface IProps{
    activeChat: any;
    toggleCollapse1: any;
    isOpen1: any;
}

const ChangeGroupNameButton : React.FC<IProps> = ({ activeChat, toggleCollapse1, isOpen1 }) => {
    const { t } = useTranslation();
    
    return <Card className="shadow-none border mb-2">
    <CustomCollapse
      title="About"
      iconClass="ri-user-2-line"
      isOpen={isOpen1}
      toggleCollapse={toggleCollapse1}
    >
      <div>
        <p className="text-muted mb-1">
          {activeChat.groupId ? "Group name:" : t("Name:")}
        </p>
        <h5 className="font-size-14">{activeChat.name}</h5>
      </div>

      <div className="text-muted">
        <p className="mb-4">
          {activeChat.userDescription === undefined &&
          !activeChat.groupId
            ? t("Hey! I'm on Squad!")
            : activeChat.userDescription}
        </p>
      </div>
      {activeChat.email && (
        <div className="mt-4">
          <p className="text-muted mb-1">{t("Email")}</p>
          <h5 className="font-size-14">{activeChat.email}</h5>
        </div>
      )}
      {activeChat.department && (
        <div className="mt-4">
          <p className="text-muted mb-1">{t("Department")}</p>
          <h5 className="font-size-14 mb-0">
            {activeChat.department}
          </h5>
        </div>
      )}
    </CustomCollapse>
  </Card>
}

export default ChangeGroupNameButton;
