import React, { useState } from "react";
import {
  Button,
  UncontrolledTooltip,
  Input,
  InputGroup,
  InputGroupAddon,
} from "reactstrap";

import { useTranslation } from "react-i18next";
import ModalNewContacts from "./ModalNewContacts";
import ContactList from "./ContactList";
import { useContact } from "../contexts/ContactContext";
import IContact from "../alias/IContact";

const ContactTab: React.FC = () => {
  const [showModal, setShowModal] = useState(false);
  const [search, setSearch] = useState("");
  const { contacts } = useContact();

  function toggle() {
    setShowModal(!showModal);
  }

  const searchArray = (contacts: IContact[], search: string) => {
    return contacts.filter((item) => {
      return item.name.toUpperCase().match(search.toUpperCase());
    });
  };

  const { t } = useTranslation();
  return (
    <React.Fragment>
      <ModalNewContacts show={showModal} toggle={toggle} />

      <div>
        <div className="p-4">
          <div className="user-chat-nav float-right">
            <div id="add-contact">
              {/* Button trigger modal */}
              <Button
                type="button"
                color="link"
                onClick={toggle}
                className="text-decoration-none text-muted font-size-18 py-0"
              >
                <i className="ri-user-add-line"></i>
              </Button>
            </div>
            <UncontrolledTooltip target="add-contact" placement="bottom">
              {t("Add Contacts")}
            </UncontrolledTooltip>
          </div>
          <h4 className="mb-4">{t("Contacts")}</h4>

          <div className="search-box chat-search-box">
            <InputGroup size="lg" className="bg-light rounded-lg">
              <InputGroupAddon addonType="prepend">
                <Button
                  color="link"
                  className="text-decoration-none text-muted pr-1"
                  type="button"
                >
                  <i className="ri-search-line search-icon font-size-18"></i>
                </Button>
              </InputGroupAddon>
              <Input
                type="text"
                className="form-control bg-light "
                placeholder={t("Search users..")}
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </InputGroup>
          </div>
          {/* End search-box */}
        </div>
        {/* end p-4 */}

        <ContactList recentContactList={searchArray(contacts, search)} />
      </div>
    </React.Fragment>
  );
};

export default ContactTab;
