import API from "@digivox/apicommunicator";
import {
  AddConferenceParticipantCallback,
  ConferenceRoomParticipant,
  CreateConferenceCallback,
  CreateConferenceRequest,
} from "@digivox/apicommunicator/dist/types/types/types";
import { IConference } from "../../alias/IConference";
import { parseConferences } from "../utils/parseUtils";
import SettingsBusiness from "./Settings";

interface ConferencesCallback {
  (contacts: IConference[]): void;
}
export enum ConferenceRoomParticipantType {
  PARTICIPANT = "PARTICIPANT",
  PARTICIPANT_EXTERNAL = "PARTICIPANT_EXTERNAL",
  MODERATOR = "MODERATOR",
}
export default interface ConferencesBusiness {}
export default class ConferencesBusiness {
  constructor(private api: API, private settings: SettingsBusiness) {}
  list = (cb: ConferencesCallback) => {
    this.api.listConferenceRooms({}, (conferences) => {
      this.settings.get((settings) => {
        cb(parseConferences(conferences.conferenceRooms, settings));
      });
    });
  };
  create = (
    conference: CreateConferenceRequest,
    cb: CreateConferenceCallback
  ) => {
    this.api.createConference(conference, cb);
  };
  addParticipants = (
    conferenceId: string,
    participants: ConferenceRoomParticipant[],
    cb: AddConferenceParticipantCallback
  ) => {
    this.api.addConferenceParticipant(conferenceId, participants, cb);
  };
}
