import React, { useEffect } from "react";
import { motion } from "framer-motion";
import { Button } from "reactstrap";
import { useCall } from "./../contexts/CallContext";
import CallStatus from "../enuns/CallStatus";
import ReactImageFallback from "react-image-fallback";
import defaultAvatar from "../assets/images/default.png";
import { useContact } from "../contexts/ContactContext";
import IGroup from "../alias/IGroup";
import IContact from "../alias/IContact";
import { useGroup } from "../contexts/GroupContext";

function width() {
  return (
    window.innerWidth ||
    document.documentElement.clientWidth ||
    document.getElementsByTagName("body")[0].clientWidth
  );
}

function height() {
  return (
    window.innerHeight ||
    document.documentElement.clientHeight ||
    document.getElementsByTagName("body")[0].clientHeight
  );
}

var audio = new Audio();

const IncomingCall: React.FC = () => {
  const { show, setShow, currentCall } = useCall();
  const { openContact } = useContact();
  const { openGroup } = useGroup();

  const open = () => {
    setShow(true);
    audio.play();
  };

  const close = () => {
    setShow(false);
    audio.pause();
  };

  const acceptCall = (video = false) => {
    if (currentCall && currentCall.accept) {
      if ((currentCall.callerObject as IGroup).groupId) {
        openGroup(currentCall.callerObject as IGroup);
      } else {
        openContact(currentCall.callerObject as IContact);
      }
      currentCall.accept(video);
      close();
    }
  };

  const rejectCall = () => {
    if (currentCall && currentCall.endCall) {
      currentCall.endCall();
    }
  };

  const init = () => {
    if (currentCall?.callStatus === CallStatus.RINGING) {
      audio.loop = true;
      audio.src =
        "https://m.toqueparacelular.com.br/a/z2/Ascent_Alarm_Loud.mp3";
      audio.load();
      open();
    }

    if (!currentCall) {
      close();
    }
  };

  useEffect(init, [currentCall]);

  if (!show) return <></>;

  return (
    <motion.div
      initial={{ bottom: "-150px" }}
      animate={{ bottom: "30px" }}
      transition={{ duration: 1 }}
      drag
      dragConstraints={{
        top: (height() - 210) * -1,
        bottom: 0,
        left: (width() - 260) * -1,
        right: 0,
      }}
      style={{
        position: "fixed",
        bottom: "30px",
        right: "30px",
        border: "4px solid #ffffff",
        backgroundColor: "rgba(255,255,255,.7)",
        width: "200px",
        height: "150px",
        zIndex: 100,
        borderRadius: "3px",
        boxShadow: "0 5px 5px 5px rgba(0,0,0,0.03)",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
          height: "60%",
        }}
      >
        {currentCall?.callerProfilePicture ? (
          <div className="mr-3">
            <ReactImageFallback
              src={currentCall?.callerProfilePicture}
              style={{ maxHeight: "40px", maxWidth: "40px" }}
              fallbackImage={defaultAvatar}
              className="rounded-circle avatar-lg img-thumbnail"
              alt="chatvia"
            />
          </div>
        ) : (
          <div className="chat-user-img align-self-center mr-3">
            <div className="avatar-xs">
              <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                {currentCall?.callerName?.trim().charAt(0) || "C"}
              </span>
            </div>
          </div>
        )}
        <h6>{currentCall?.callerName || currentCall?.callerNumber}</h6>
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "40%",
        }}
      >
        <Button
          type="button"
          color="none"
          onClick={() => acceptCall()}
          className="nav-btn user-profile-show bg-primary mr-2"
          style={{
            color: "#ffffff",
          }}
        >
          <i className="ri-phone-line"></i>
        </Button>
        <Button
          type="button"
          color="none"
          onClick={() => acceptCall(true)}
          className="nav-btn user-profile-show bg-success"
          style={{
            color: "#ffffff",
          }}
        >
          <i className="ri-vidicon-line"></i>
        </Button>
        <Button
          type="button"
          color="none"
          onClick={() => rejectCall()}
          className="nav-btn user-profile-show bg-danger ml-2"
          style={{
            color: "#ffffff",
          }}
        >
          <i className="ri-close-line"></i>
        </Button>
      </div>
    </motion.div>
  );
};

export default IncomingCall;
