import React, { useState } from "react";
import { Media, Button, Row, Col, Tooltip } from "reactstrap";
import { Link } from "react-router-dom";
import { useChat } from "../contexts/ChatContext";
import ReactImageFallback from "react-image-fallback";
import { onImgError } from "../helpers/ImageUtils";
import { useGroup } from "../contexts/GroupContext";
import defaultAvatar from "../assets/images/default.png";
import { useContact } from "../contexts/ContactContext";
import { useModal } from "../contexts/ModalContext";
import { useAuth } from "../contexts/AuthContext";
import { useCall } from "../contexts/CallContext";
import { useConference } from "../contexts/ConferenceContext";
import ModalAddParticipant from "./ModalAddParticipant";
import ScreenType from "../enuns/ScreenType";
import { useMainBox } from "../contexts/MainBoxContext";
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import CopyParticipantLinkButton from "./CopyParticipantLinkButton";
import CopyModeratorLinkButton from './CopyModeratorLinkButton';
import { useTranslation } from "react-i18next";

interface IProps {
  chatProfileSidebarOpen: boolean;
  setChatProfileSidebarOpen(value: boolean): void;
}

const ChatHead: React.FC<IProps> = ({
  chatProfileSidebarOpen,
  setChatProfileSidebarOpen,
}) => {
  const { user } = useAuth();
  const { t } = useTranslation();
  const { chats, activeChat, setChats } = useChat();
  const { groups, setGroups } = useGroup();
  const { contacts, setContacts } = useContact();
  const { conferenceList } = useConference();
  const { setShowModalAddMember, setShowModalAddParticipant } = useModal();
  const { setCallNumber } = useCall();
  const { setScreen } = useMainBox();
  const ChatProfileSidebar = () => {
    setChatProfileSidebarOpen(!chatProfileSidebarOpen);
  };

  const { activeConference } = useConference();

  const [tooltipOpen1, setTooltipOpen1] = useState(false);
  const [tooltipOpen2, setTooltipOpen2] = useState(false);
  const [tooltipOpen3, setTooltipOpen3] = useState(false);
  const [tooltipOpen4, setTooltipOpen4] = useState(false);
  const [tooltipOpen5, setTooltipOpen5] = useState(false);
  const [tooltipOpen6, setTooltipOpen6] = useState(false);

  const toggleTooltip1 = () => setTooltipOpen1(!tooltipOpen1);
  const toggleTooltip2 = () => setTooltipOpen2(!tooltipOpen2);
  const toggleTooltip3 = () => setTooltipOpen3(!tooltipOpen3);
  const toggleTooltip4 = () => setTooltipOpen4(!tooltipOpen4);
  const toggleTooltip5 = () => setTooltipOpen5(!tooltipOpen5);
  const toggleTooltip6 = () => setTooltipOpen6(!tooltipOpen6);

  const makeCall = (useVideo: boolean) => {
    let contact = contacts.find((item) => item.jid === activeChat.jid);
    let group = groups.find((gr) => gr.jid === activeChat.jid);
    let conference = conferenceList.find((conf) => conf.jid === activeChat.jid);
    let number;
    if (contact) number = contact?.number;
    else if (group)
      number = `citrus-conference-authenticated-${group?.groupId}`;
    else if (conference) {
      if (
        conference.conferenceRoomParticipants.find(
          (part) => part.userUuid === user.id
        )?.userModerator
      )
        number = `citrus-conference_${conference.id}_${conference.moderatorPin}`;
      else
        number = `citrus-conference_${conference.id}_${conference.participantPin}`;
    }
    if (number) {
      setCallNumber({
        number,
        useAudio: true,
        useVideo,
      });
    }
  };
  function closeUserChat() {
    setScreen(ScreenType.CHAT);
  }
  const isAdmin =
    activeChat.members?.find((member) => member.userId === user.id)?.role ===
    "admin";
    
  const subtitleGroupMembers = activeChat.members;

  return (
    <React.Fragment>
      <ModalAddParticipant />
      <ToastContainer 
      position="top-center"
      
      autoClose={2000}
      hideProgressBar={false}
      newestOnTop={false}
      closeOnClick
      rtl={false}
      pauseOnFocusLoss
      pauseOnHover/>
      <div className="p-3 p-lg-4 border-bottom">
        <Row className="align-items-center">
          <Col sm={4} xs={8}>
            <Media className="align-items-center">
              <div className="d-block d-lg-none mr-2">
                <Link
                  to="#"
                  onClick={() => closeUserChat()}
                  className="user-chat-remove text-muted font-size-16 p-2"
                >
                  <i className="ri-arrow-left-s-line"></i>
                </Link>
              </div>
              {activeChat.profilePicture ? (
                <div className="mr-3">
                  <ReactImageFallback
                    src={activeChat.profilePicture}
                    onError={() => {
                      if (activeChat.jid.includes("@conference."))
                        onImgError(
                          chats,
                          groups,
                          contacts,
                          setChats,
                          setGroups,
                          setContacts,
                          activeChat
                        );
                    }}
                    style={{ maxHeight: "40px", maxWidth: "40px" }}
                    fallbackImage={defaultAvatar}
                    className="rounded-circle avatar-lg img-thumbnail"
                    alt="chatvia"
                  />
                </div>
              ) : (
                <div className="chat-user-img align-self-center mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {activeChat.name.trim().charAt(0)}
                    </span>
                  </div>
                </div>
              )}

              <Media body className="overflow-hidden">
                <h5 className="font-size-16 text-truncate" style={{display: "flex", flexDirection: "row", alignItems: "center"}}>
                  <Link
                    to="#"
                    onClick={() => ChatProfileSidebar()}
                    className="text-reset user-profile-show"
                  >
                    {activeChat.name}
                    <div className={"text-truncate"} 
                    style={{
                      fontWeight: "lighter",
                      color: "gray",
                      fontSize: 12,
                      marginTop: 5,
                      marginLeft: 1,
                    }}>
                      {subtitleGroupMembers?.map((gpmbr) => {
                        return(
                          gpmbr.name.split(' ')[0] + '; '
                        )
                      })}
                    </div>
                  </Link>
                  {(() => {
                    switch (activeChat.status) {
                      case "online":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-success d-inline-block ml-1" style={{marginBottom: 3}}></i>
                          </>
                        );

                      case "away":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-warning d-inline-block ml-1" style={{marginBottom: 3}}></i>
                          </>
                        );

                      case "offline":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-secondary d-inline-block ml-1" style={{marginBottom: 3}}></i>
                          </>
                        );

                      default:
                        return;
                    }
                  })()}
                </h5>
              </Media>
            </Media>
          </Col>
          <Col sm={8} xs={4}>
            <ul className="list-inline user-chat-nav text-right mb-0">
              {activeChat.jid.includes("@conference.") && isAdmin && (
                <li className="list-inline-item d-none d-lg-inline-block">
                  <Button
                    type="button"
                    color="none"
                    onClick={() => setShowModalAddMember(true)}
                    className="nav-btn user-profile-show"
                  >
                    <i className="ri-user-add-line"></i>
                  </Button>
                </li>
              )}
              {activeChat.conferenceRoomParticipants && (
                <>
                  <li id="TooltipExample1" className="list-inline-item d-none d-lg-inline-block">
                    <CopyParticipantLinkButton participantLink={activeConference?.anonymousParticipantUrl as string} buttonIcon={"ri-links-line"} />
                  </li>
                  <li id="TooltipExample2" className="list-inline-item d-none d-lg-inline-block">
                    <CopyModeratorLinkButton moderatorLink={activeConference?.anonymousModeratorUrl as string} buttonIcon={"ri-link-unlink"} />
                  </li>
                  <li id="TooltipExample3" className="list-inline-item d-none d-lg-inline-block">
                    <Button
                      type="button"
                      color="none"
                      onClick={() => {
                        setShowModalAddParticipant(true);
                      }}
                      className="nav-btn user-profile-show"
                    >
                      <i className="ri-user-add-line"></i>
                    </Button>
                  </li>
                </>
              )}
              <li id="TooltipExample4" className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    makeCall(false);
                  }}
                  className="nav-btn user-profile-show"
                >
                  <i className="ri-phone-line"></i>
                </Button>
              </li>
              <li id="TooltipExample5" className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    makeCall(true);
                  }}
                  className="nav-btn user-profile-show"
                >
                  <i className="ri-vidicon-line"></i>
                </Button>
              </li>
              <li id="TooltipExample6" className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => ChatProfileSidebar()}
                  className="nav-btn user-profile-show"
                >
                  <i className="ri-menu-line"></i>
                </Button>
              </li>
            </ul>
          </Col>
        </Row>
      </div>
      <Tooltip
        placement="right"
        isOpen={tooltipOpen1}
        target="TooltipExample1"
        toggle={toggleTooltip1}
      >
        {t(
          "Copy participant invitation link"
        )}
      </Tooltip>
      <Tooltip
        placement="right"
        isOpen={tooltipOpen2}
        target="TooltipExample2"
        toggle={toggleTooltip2}
      >
      {t(
          "Copy moderator invitation link"
        )}
      </Tooltip>

      <Tooltip
        placement="right"
        isOpen={tooltipOpen3}
        target="TooltipExample3"
        toggle={toggleTooltip3}
      >
        {t(
          "Add member"
        )}
      </Tooltip>

      <Tooltip
        placement="right"
        isOpen={tooltipOpen4}
        target="TooltipExample4"
        toggle={toggleTooltip4}
      >
        {t("Start voice call")}
      </Tooltip>

      <Tooltip
        placement="right"
        isOpen={tooltipOpen5}
        target="TooltipExample5"
        toggle={toggleTooltip5}
      >
        {t("Start video conference")}
      </Tooltip>

      <Tooltip
        placement="right"
        isOpen={tooltipOpen6}
        target="TooltipExample6"
        toggle={toggleTooltip6}
      >
        {t("Open menu")}
      </Tooltip>

    </React.Fragment>
  );
};

