import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Collapse,
  CardHeader,
  CardBody,
  Card,
} from "reactstrap";

//simple bar
import SimpleBar from "simplebar-react";

//components
import SelectContact from "./SelectContact";

import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";

import { useAuth } from "../contexts/AuthContext";
import { useChat } from "../contexts/ChatContext";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import IMessage from "../alias/IMessage";

interface IProps {
  show: boolean;
  toggle(): void;
  message : IMessage[];
/*   activeChat: IChat;
  selectedMessage: any; */
}

export const ModalSelectUsers: React.FC<IProps> = ({ show, toggle, message /* activeChat, selectedMessage */}) => {
  const { t } = useTranslation();

  const { activeChat, setActiveChat } = useChat();
  const { user } = useAuth();
  const [ textMessage, setTextMessage ] = useState("");

  const [isOpenCollapse] = useState(true);
  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);
  

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      let selected = selectedContact;
      selected.push(contact);
      setSelectedContact(selected);
      //handleSendMessage(preciso referenciar a text message que vai ser encaminhada aqui);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };

  const handleSendMessage = () => {
    if (textMessage !== "") {
      let chat = { ...activeChat };

      chat.messages.push({
        id: `${activeChat.messages.length + 1}`,
        userName: user.name,
        message: textMessage,
        time: "",
        isImageMessage: false,
        isFileMessage: false,
        isVideoMessage: false,
        isAudioMessage: false,
        fileUrl: "",
        sendFile: undefined,
        toUser: activeChat.id,
        fromUser: user.id,
        deliverStatus: DeliverStatus.SENDING,
        readStatus: ReadStatus.UNREAD,
        deliverTimestamp: "",
        readTimestamp: "",
        content: "metadata",
        contentType: "type",
      });

      setActiveChat(chat);
      setTextMessage("");
    }
  };

  return (
    <Modal isOpen={show} centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        {t("Forward message")}
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <FormGroup className="mb-4">
            <Collapse isOpen={isOpenCollapse} id="groupmembercollapse">
              <Card className="border">
                <CardHeader>
                  <h5 className="font-size-15 mb-0">{t("Contacts")}</h5>
                </CardHeader>
                <CardBody className="p-2">
                  <SimpleBar style={{ maxHeight: "150px" }}>
                    <div id="addContacts">
                      <SelectContact handleCheckContact={handleCheckContact}/>
                    </div>
                  </SimpleBar>
                </CardBody>
              </Card>
            </Collapse>
          </FormGroup>
        </Form>
      </ModalBody>
      <ModalFooter>
        <Button type="button" color="link" onClick={toggle}>
          {t("Close")}
        </Button>
        <Button type="button" color="primary" onClick={() => forwardMessage()}>
          Send
        </Button>
      </ModalFooter>
    </Modal>
  );
};
