import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { Card, Media, Badge, Button } from 'reactstrap';
import ReactImageFallback from "react-image-fallback";
import { Link } from "react-router-dom";
import { useContact } from '../contexts/ContactContext';
import { useAuth } from '../contexts/AuthContext';
import { useChat } from '../contexts/ChatContext';
import { useGroup } from '../contexts/GroupContext';
import Swal from 'sweetalert2'
import IChat from '../alias/IChat';
import IMember from '../alias/IMember';
import { ConferenceRoomParticipantType } from '../services/business/Conferences';
import { useConference } from '../contexts/ConferenceContext';
import { ConferenceRoomParticipant } from '@digivox/apicommunicator/dist/types/types/types';
import ScreenType from "../enuns/ScreenType";
import { useMainBox } from "../contexts/MainBoxContext";
import ModalGenerics from './ModalGenerics';
import { toast } from 'react-toastify';
import { toastSucessFunction } from './../helpers/toastSucessFunction';

interface IProps {
  member: IMember;
  defaultAvatar: any;
  isAdmin: any;
}

const ChangeGroupNameButton : React.FC<IProps> = ({ isAdmin, defaultAvatar, member, }) => {
    const { activeChat, chats, openChat } = useChat();
    const [show, setShow] = useState<boolean>(false);
    const { setToggleMemberRole, setRemovedMember, setGroupToRemoveContact } = useGroup();
    const { setClickedContact, contacts, contactsOutsideUserContacts, openContact } = useContact();
    const { setRemovingParticipantsPayload, setAddingParticipantsPayload } = useConference();
    const { setScreen } = useMainBox();
    const [arrayToPushAOnlyParticipant, setArrayToPushAOnlyParticipant] = useState<ConferenceRoomParticipant[]>([]);
    const { user } = useAuth();
    const { t } = useTranslation();
    const moderators = activeChat
    .conferenceRoomParticipants?.filter(
      (participant) => 
          participant.userModerator === true && 
          participant.type === ConferenceRoomParticipantType.MODERATOR &&
          participant.userUuid === user.id
    );
    const amIaModerator =
    activeChat.conferenceRoomParticipants?.find((member) => member.userUuid === user.id)?.userModerator ===
    true;

    const isThisMemberAModerator = (member: IMember) => {
      moderators?.find((moderator) => moderator.userUuid === member.userId)
    }

    const openChatAction = (chat: IChat) => {
        openChat(chat);
        setScreen(ScreenType.CHAT);
    };

    const toggleMemberRole = (member: IMember, activeChat: IChat) => {
      Swal.fire({
        position: 'center',
        icon: 'success',
        title: member.name + " " + t("is now a member!"),
        showConfirmButton: false,
        timer: 1500,
        customClass: {
          popup: "swal-mini-popup"
        }
      })
      setToggleMemberRole({
        mbId: member.userId,
        gpId: activeChat.groupId
          ? activeChat.groupId
          : "",
        mbRole: member.role,
        mb: member,
      });
    }

    const toggleParticipantRole = (conferenceId: string, participant: IMember) => {
          
      arrayToPushAOnlyParticipant.push(
        {
        description: '',
            email: '',
            name: participant.name,
            userModerator: participant.role === "admin" ? false : true,
            type: participant.role === "admin" ? ConferenceRoomParticipantType.PARTICIPANT : ConferenceRoomParticipantType.MODERATOR,
            userUuid: participant.userId
        } as ConferenceRoomParticipant
      )
      setRemovingParticipantsPayload({
        conferenceId: conferenceId,
        participantId: participant.userId
      });
      setTimeout(() => {
        setAddingParticipantsPayload({
          conferenceId: conferenceId,
          data: arrayToPushAOnlyParticipant,
        });
        setArrayToPushAOnlyParticipant([]);
      }, 2500)
    }

    const removeParticipantPayload = (activeChat: IChat, member: IMember) => {
      setRemovingParticipantsPayload({
        conferenceId: activeChat.id,
        participantId: member.userId,
      })
    }
    
    const removeParticipantAndToastSuccessAndCloseModal = (activeChat: IChat, member: IMember) => {
      removeParticipantPayload(activeChat, member);
      toastSucessFunction();
      setShow(!show);
    }

    const removeMemberPayload = (member: IMember, activeChat: IChat) => {
      setRemovedMember(member)
      setGroupToRemoveContact(activeChat.groupId)
    }

    const removeMemberAndToastAndCloseModal = (member: IMember, activeChat: IChat) => {
      removeMemberPayload(member, activeChat);
      toastSucessFunction();
      setShow(!show);
    }

    return (<>
    <ModalGenerics
      setShow={setShow}
      show={show}
      title={t("Remove") + " " + member.name + "?"}
      children={
        <div>
          <>
          <Button type="button" color="primary" onClick={
            activeChat.conferenceRoomParticipants 
              ? 
            () => removeParticipantAndToastSuccessAndCloseModal(activeChat, member)
                : 
            () => removeMemberAndToastAndCloseModal(member, activeChat)
          }>{t("Confirm")}</Button>
          <Button type="button" color="link" onClick={() => 
            {
              setShow(!show);
            }
          }>
            {t("Cancel")}
          </Button>
          </>
        </div>
      }
      />
    <Card key={member.userId} className="p-2 mb-2">
    <Media className="align-items-center">
      <ReactImageFallback
        src={member.profilePicture}
        className="rounded-circle avatar-xs mr-2"
        alt="chatvia"
        fallbackImage={defaultAvatar}
      />
      <Media body>
        <div className="text-left">
          <h5 className="font-size-14 mb-1">
            <Link
              to="#"
              onClick={() => {
                chats.forEach((cht: any) => {
                  if (cht.id === member.userId) {
                    openChatAction(cht);
                    contacts.forEach((ctc: any) => {
                      if (ctc.id === member.userId) {
                        setClickedContact(ctc);
                      }
                    });
                  } else {
                    contactsOutsideUserContacts.forEach(
                      (ctcOut) => {
                        if (ctcOut.id === member.userId) {
                          openContact(ctcOut);
                          setClickedContact(ctcOut);
                        }
                      });
                    }
                  });
                }}
              >
                {member.name}
              </Link>

            {(((isAdmin || amIaModerator) && user.id !== member.userId) && (
              <span
                className="float-right ml-2"
                style={{ cursor: "pointer" }}
                onClick={() =>
                  setShow(true)
                }
              >
                <i className="ri-close-fill"></i>
              </span>
            ))}
            {member.role === "admin" || isThisMemberAModerator(member) ?
              (
              <Badge
                color={activeChat.conferenceRoomParticipants ? "warning" : "danger"}
                className={activeChat.conferenceRoomParticipants ? "badge-soft-warning float-right" : "badge-soft-danger float-right"}
                onClick={() =>
                  (isAdmin || amIaModerator) && (member.userId !== user.id) &&
                  Swal.fire({
                    title:
                      t("Turn") +
                      " " +
                      member.name +
                      " " +
                      t("a member?"),
                    text: "",
                    icon: "warning",
                    showCancelButton: true,
                    confirmButtonColor: "#4e5e96",
                    cancelButtonColor: "#d33",
                    cancelButtonText: t("No"),
                    confirmButtonText: t("Yes"),
                    customClass: {
                      popup: "swal-mini-popup"
                    }
                  }).then((result) => {
                    if (result.isConfirmed) {
                      Swal.fire({
                        position: 'center',
                        icon: 'success',
                        title: member.name + " " + t("is now a member!"),
                        showConfirmButton: false,
                        timer: 1500,
                        customClass: {
                          popup: "swal-mini-popup"
                        }
                      })
                    }
                    activeChat.conferenceRoomParticipants ?

                      toggleParticipantRole(activeChat.id, member)

                    : 
                      toggleMemberRole(member, activeChat)
                  })
                }
                style={{ cursor: "pointer" }}
              >
                {activeChat.conferenceRoomParticipants ? t("Moderator") : t("Admin")}
              </Badge>
              ) : (
              <Badge
                color="success"
                className="badge-soft-success float-right"
                style={{ cursor: "pointer" }}
                onClick={() =>
                  (isAdmin || amIaModerator) && (member.userId !== user.id) &&
                  Swal.fire({
                    title:
                      t("Turn") +
                      " " +
                      member.name +
                      " " +
                      t("an administrator?"),
                    text: "",
                    icon: "warning",
                    showCancelButton: true,
                    confirmButtonColor: "#4e5e96",
                    cancelButtonColor: "#d33",
                    cancelButtonText: t("No"),
                    confirmButtonText: t("Yes"),
                    customClass: {
                      popup: "swal-mini-popup"
                    }
                  }).then((result) => {
                    if (result.isConfirmed) {
                      Swal.fire({
                        position: 'center',
                        icon: 'success',
                        title: member.name + " " + t("is now an administrator!"),
                        showConfirmButton: false,
                        timer: 1500,
                        customClass: {
                          popup: "swal-mini-popup"
                        }
                      })
                      activeChat.conferenceRoomParticipants ?
                      toggleParticipantRole(activeChat.id, member)
                      : 
                      toggleMemberRole(member, activeChat)
                    }
                  })
                }
              >
                {t("Member")}
              </Badge>
            )}

          </h5>
        </div>
      </Media>
    </Media>
  </Card>
  </>
  );
}

export default ChangeGroupNameButton;
