import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Collapse,
  CardHeader,
  CardBody,
  Card,
} from "reactstrap";

//simple bar
import SimpleBar from "simplebar-react";

//components
import SelectContact from "./SelectContact";

import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";

import { useChat } from "../contexts/ChatContext";
/* import IChat from "../alias/IChat"; */
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import { useAuth } from "../contexts/AuthContext";

interface IProps {
  show: boolean;
  toggle(): void;
}

export const ModalSelectUsers: React.FC<IProps> = ({ show, toggle }) => {
  const { t } = useTranslation();

  const [isOpenCollapse, setIsOpenCollapse] = useState(true);
  /* const [isOpenAlert, setIsOpenAlert] = useState(false);
  const [message, setMessage] = useState("");
  const [groupName, setGroupName] = useState("");
  const [groupDescription, setGroupDescription] = useState(""); */
  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);

  const { chats, setChats, activeChat } = useChat();
  const { user } = useAuth();
  /*   const [activeChatCurrent, setActiveChatCurrent] = useState<IChat | null>(
      null
    ); */
  const [textMessage, setTextMessage] = useState("");

  // como armazenar a mensagem clicada pelo usuário nos três pontinhos e setar como textMessage aqui na onAddMessage?
  
  /* const onAddMessage = () => {
    if (textMessage !== "") {
      setChats(
        chats.map((item) => {
          if (activeChat)
            if (item.id === activeChat?.id) {
              item.messages.push({
                id: `${item.messages.length + 1}`,
                userName: user.name,
                message: textMessage,
                time: "",
                isImageMessage: false,
                isFileMessage: false,
                isVideoMessage: false,
                isAudioMessage: false,
                fileUrl: "",
                sendFile: undefined,
                toUser: activeChat.id,
                fromUser: user.id,
                deliverStatus: DeliverStatus.SENDING,
                readStatus: ReadStatus.UNREAD,
                deliverTimestamp: "",
                readTimestamp: "",
                content: "metadata",
                contentType: "type",
              });
            }
          return item;
        })
      );
      setTextMessage("");
    }
  };

  const toggleCollapse = () => {
    setIsOpenCollapse(!isOpenCollapse);
  }; */

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      let selected = selectedContact;
      selected.push(contact);
      setSelectedContact(selected);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };

  return (
    <Modal isOpen={show} centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        {t("Forward message")}
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <FormGroup className="mb-4">
            <Collapse isOpen={isOpenCollapse} id="groupmembercollapse">
              <Card className="border">
                <CardHeader>
                  <h5 className="font-size-15 mb-0">{t("Contacts")}</h5>
                </CardHeader>
                <CardBody className="p-2">
                  <SimpleBar style={{ maxHeight: "150px" }}>
                    <div id="addContacts">
                      <SelectContact handleCheckContact={handleCheckContact} />
                    </div>
                  </SimpleBar>
                </CardBody>
              </Card>
            </Collapse>
          </FormGroup>       
        </Form>
      </ModalBody>
      <ModalFooter>
        <Button type="button" color="link" onClick={toggle}>
          {t("Close")}
        </Button>
        <Button type="button" color="primary" onClick={() => alert('Mensagem enviada!')}>
          Send  
        </Button>
      </ModalFooter>
    </Modal>
  );
};
