import React, { useState } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
} from "reactstrap";

import { useTranslation } from "react-i18next";
import { useModal } from "./../contexts/ModalContext";
import { useAuth } from './../contexts/AuthContext';

export const ModalChangePassword: React.FC = () => {
  const { t } = useTranslation();

  const { setChangedUserPassword } = useAuth();
  const { showModalChangePassword, setShowModalChangePassword } = useModal();
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");

  const toggle = () => {
    setShowModalChangePassword(!showModalChangePassword);
  };

  
  const handleCheckPassword = () => {
    if(currentPassword === newPassword){
      return false;
    }else{
      return true;
    } 
  }
  
  const handleCheckNewPassword = () => {
    if(newPassword !== confirmNewPassword){
      return false;
    }else{
      return true;
    }
  };

  const checkAllPasswords = () => {
    handleCheckPassword();
    handleCheckNewPassword();
  }
  
  return (
    <Modal isOpen={showModalChangePassword} centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        Alterar Senha
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <FormGroup className="mb-4">
            <Label htmlFor="current-input">Senha atual</Label>
            <Input
              type="password"
              className="form-control"
              id="current-input"
              defaultValue={""}
              onChange={(e) => setCurrentPassword(e.target.value)}
              placeholder="Senha atual"
            />
          </FormGroup>
          <FormGroup className="mb-4">
            <Label htmlFor="new-input">Nova senha</Label>
            <Input
              type="password"
              className="form-control"
              id="new-input"
              defaultValue={""}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Nova senha"
            />
          </FormGroup>
          <FormGroup className="mb-4">
            <Label htmlFor="confirm-input">Confirmar nova senha</Label>
            <Input
              type="password"
              className="form-control"
              id="confirm-input"
              defaultValue={""}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
              placeholder="Confirmar nova senha"
            />
          </FormGroup>
        </Form>
      </ModalBody>
      <ModalFooter>
        <Button
          type="button"
          color="link"
          onClick={() => setShowModalChangePassword(false)}
        >
          {t("Close")}
        </Button>
        <Button type="button" color="primary" onClick={() => {
          /* setChangedUserPassword({ oldPassword: currentPassword, newPassword: newPassword }); */
          setShowModalChangePassword(false);
          checkAllPasswords();
        }
        }>
          Alterar Senha
        </Button>
      </ModalFooter>
      <div style={{color: "red", fontSize: 12, display: "flex", flexDirection: "row", justifyContent: "flex-end"}}> 
          {handleCheckPassword() ? "" : "A senha atual corresponde à nova solicitada!"}
          {handleCheckNewPassword() ? "" : "A confirmação da nova senha não corresponde!"}
        </div>
    </Modal>
  );
};
