import React, { useEffect, useState } from "react";
import { Button, Input } from "reactstrap";
import { useMainBox } from "../contexts/MainBoxContext";
import ScreenType from "../enuns/ScreenType";
import './../assets/scss/welcome.scss';
import { motion, AnimatePresence } from "framer-motion"

const Welcome: React.FC = () => {
  const { setScreen } = useMainBox();
  const [isVisible, setIsVisible] = useState(false);
  useEffect(() => {
    setTimeout(() => {
      setIsVisible(true);
    }, 4000)
  }, [])

  return (
    
    <div className={"main"}>
      <AnimatePresence>
        {isVisible && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          />
        )}
      </AnimatePresence>
      <img 
        style={{
          backgroundSize: 'contain',
          background: 'no-repeat',
          width:'45%',
          height: 'auto',
        }}
        src={require('./../assets/images/negocio.png')}
        alt={"decisions"}
      />
      
      <div className={"form shadow bg-white rounded"}>

        <div className={"inputs"}>

          <h1>Entre já!</h1>
        
          <h6 style={{
            fontSize: 14, 
            marginTop: 20, 
            marginBottom: 30
          }} 
          className="text-muted">E conheça a melhor plataforma de cotações virtuais!</h6>
          
          <Input style={{}} placeholder="E-mail"></Input>

          <Input style={{marginTop: 10}} placeholder="Senha"></Input>
        
        </div>

        <div style={{
          marginTop: 20,
        }} 
        className={"buttons"}>
          <Button color={"primary"} style={{
            width: "100%"
          }}
          onClick={() => setScreen(ScreenType.MINHASCOTACOES)}>
            Entrar
          </Button>
          <Button style={{
            width: '100%',
            marginTop: 10
          }}
            color={"light"}>
            Cadastre-se
          </Button>
          <Button style={{
            float: "right",
            fontSize: 14  
          }}
            color={"link"}>
            Esqueceu a sua senha?
          </Button>
        </div>
      </div>
    </div>
  );
};

export default Welcome;
