import React from "react";
import { Media } from "reactstrap";
import { Link } from "react-router-dom";

//simplebar
import SimpleBar from "simplebar-react";

//context
import { useChat } from "./../contexts/ChatContext";
import { useContact } from "./../contexts/ContactContext";

//alias
import IChat from "../alias/IChat";

import goldenstar from "./../assets/images/ic-star-fill.svg";
import star from "./../assets/images/ic-star.svg";
import ReactImageFallback from "react-image-fallback";
import defaultAvatar from "../assets/images/default.png";
import { useGroup } from "../contexts/GroupContext";
import { onImgError } from "../helpers/ImageUtils";
import { useTranslation } from "react-i18next";
import ChatListDates from "./ChatListDates";
import { TextMessageTagParseNotClickable } from "../helpers/TextMessageTagParseNotClickable";
import { useMainBox } from "../contexts/MainBoxContext";
import ScreenType from "../enuns/ScreenType";

interface IProps {
  recentChatList: IChat[];
}

const ChatList: React.FC<IProps> = ({ recentChatList }) => {
  const { activeChat, chats, setChats, openChat } = useChat();
  const { contacts, setContacts } = useContact();
  const { groups, setGroups } = useGroup();
  const { t } = useTranslation();
  const { setNewFavorite } = useContact();
  const { setScreen } = useMainBox();

  const openChatAction = (chat: IChat) => {
    openChat(chat);
    setScreen(ScreenType.CHAT);
  };

  return (
    <SimpleBar style={{ maxHeight: "100%" }} className="chat-message-list">
      <ul className="list-unstyled chat-list chat-user-list" id="chat-list">
        {recentChatList.map(
          (chat, key) =>
            chat.favorite && (
              <li
                key={key}
                id={"conversation" + key}
                className={`${chat.isTyping ? "typing" : ""} ${
                  chat.id === activeChat.id ? "active" : ""
                }`}
              >
                <Link to="#" onClick={() => openChatAction(chat)}>
                  <Media>
                    {!chat.profilePicture ? (
                      <div
                        className={
                          "chat-user-img " +
                          chat.status +
                          " align-self-center mr-3"
                        }
                      >
                        <div className="avatar-xs">
                          <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                            {chat.name.charAt(0)}
                          </span>
                        </div>
                        {chat.status && <span className="user-status"></span>}
                      </div>
                    ) : (
                      <div
                        className={
                          "chat-user-img " +
                          chat.status +
                          " align-self-center mr-3"
                        }
                      >
                        <ReactImageFallback
                          src={chat.profilePicture}
                          className="rounded-circle avatar-xs"
                          alt="chatvia"
                          fallbackImage={defaultAvatar}
                          onError={() => {
                            console.log("imgError");
                            if (chat.jid.includes("@conference."))
                              onImgError(
                                chats,
                                groups,
                                contacts,
                                setChats,
                                setGroups,
                                setContacts,
                                chat
                              );
                          }}
                        />
                        {chat.status && <span className="user-status"></span>}
                      </div>
                    )}

                    <Media body className="overflow-hidden">
                      <h5 className="text-truncate font-size-15 mb-1">
                        {chat.name}
                      </h5>
                      <p className="chat-user-message text-truncate mb-0">
                        {chat.isTyping ? (
                          <>
                            {t("typing")}
                            <span className="animate-typing">
                              <span className="dot ml-1"></span>
                              <span className="dot ml-1"></span>
                              <span className="dot ml-1"></span>
                            </span>
                          </>
                        ) : (
                          <>
                            {chat.messages &&
                            chat.messages.length > 0 &&
                            chat.messages[chat.messages.length - 1]
                              .isImageMessage === true ? (
                              <i className="ri-image-fill align-middle mr-1"></i>
                            ) : null}
                            {chat.messages &&
                            chat.messages.length > 0 &&
                            chat.messages[chat.messages.length - 1]
                              .isFileMessage === true ? (
                              <i className="ri-file-text-fill align-middle mr-1"></i>
                            ) : null}
                            {chat.messages && chat.messages.length > 0
                              ? TextMessageTagParseNotClickable(
                                  chat.messages[chat.messages.length - 1]
                                    .message
                                )
                              : null}
                          </>
                        )}
                      </p>
                    </Media>

                    <img
                      alt="star"
                      onClick={() => {
                        setNewFavorite(chat);
                      }}
                      src={chat.favorite ? goldenstar : star}
                      style={{ position: "relative", right: 2, top: 2 }}
                    />

                    <div className="font-size-11">
                      <ChatListDates chat={chat} />
                    </div>
                    {/* chat.messages.findIndex((message) => message.readStatus === ReadStatus.UNREAD  && */
                    /* chat.messages.find((message) => message.message.includes(`{"userId":"`)) && message.readStatus === ReadStatus.UNREAD ? (<div>@</div>) : null} */}

                    {chat.unRead ? (
                      <div className="unread-message">
                        <span style={{ color: "#ff3535", marginRight: 2.5 }}>
                          {chat.mencioned > 0 && "@"}
                        </span>
                        <span className="badge badge-soft-danger badge-pill">
                          {chat.unRead}
                        </span>
                      </div>
                    ) : null}
                  </Media>
                </Link>
              </li>
            )
        )}

        <hr className="mx-3" />

        {recentChatList.map(
          (chat, key) =>
            chat.favorite === false && (
              <li
                key={key}
                id={"conversation" + key}
                className={`${chat.isTyping ? "typing" : ""} ${
                  chat.id === activeChat.id ? "active" : ""
                }`}
              >
                <Link to="#" onClick={() => openChatAction(chat)}>
                  <Media>
                    {!chat.profilePicture ? (
                      <div
                        className={
                          "chat-user-img " +
                          chat.status +
                          " align-self-center mr-3"
                        }
                      >
                        <div className="avatar-xs">
                          <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                            {chat.name.charAt(0)}
                          </span>
                        </div>
                        {chat.status && <span className="user-status"></span>}
                      </div>
                    ) : (
                      <div
                        className={
                          "chat-user-img " +
                          chat.status +
                          " align-self-center mr-3"
                        }
                      >
                        <ReactImageFallback
                          src={chat.profilePicture}
                          className="rounded-circle avatar-xs"
                          alt="chatvia"
                          fallbackImage={defaultAvatar}
                          onError={() => {
                            console.log("imgError");
                            if (chat.jid.includes("@conference."))
                              onImgError(
                                chats,
                                groups,
                                contacts,
                                setChats,
                                setGroups,
                                setContacts,
                                chat
                              );
                          }}
                        />
                        {chat.status && <span className="user-status"></span>}
                      </div>
                    )}

                    <Media body className="overflow-hidden">
                      <h5 className="text-truncate font-size-15 mb-1">
                        {chat.name}
                      </h5>
                      <p className="chat-user-message text-truncate mb-0">
                        {chat.isTyping ? (
                          <>
                            {t("typing")}
                            <span className="animate-typing">
                              <span className="dot ml-1"></span>
                              <span className="dot ml-1"></span>
                              <span className="dot ml-1"></span>
                            </span>
                          </>
                        ) : (
                          <>
                            {chat.messages &&
                            chat.messages.length > 0 &&
                            chat.messages[chat.messages.length - 1]
                              .isImageMessage === true ? (
                              <i className="ri-image-fill align-middle mr-1"></i>
                            ) : null}
                            {chat.messages &&
                            chat.messages.length > 0 &&
                            chat.messages[chat.messages.length - 1]
                              .isFileMessage === true ? (
                              <i className="ri-file-text-fill align-middle mr-1"></i>
                            ) : null}
                            {chat.messages && chat.messages.length > 0
                              ? TextMessageTagParseNotClickable(
                                  chat.messages[chat.messages.length - 1]
                                    .message
                                )
                              : null}
                          </>
                        )}
                      </p>
                    </Media>

                    <img
                      alt="star"
                      onClick={() => {
                        setNewFavorite(chat);
                      }}
                      src={chat.favorite ? goldenstar : star}
                      style={{ position: "relative", right: 2, top: 2 }}
                    />

                    <div className="font-size-11">
                      <ChatListDates chat={chat} />
                    </div>
                    {/* chat.messages.findIndex((message) => message.readStatus === ReadStatus.UNREAD  && */
                    /* chat.messages.find((message) => message.message.includes(`{"userId":"`)) ? (<div>@</div>) : null} */}

                    {chat.unRead ? (
                      <div className="unread-message">
                        <span className="badge badge-soft-danger badge-pill">
                          {chat.mencioned > 0 && "@"}
                          {chat.unRead}
                        </span>
                      </div>
                    ) : null}
                  </Media>
                </Link>
              </li>
            )
        )}
      </ul>
    </SimpleBar>
  );
};

export default ChatList;
