import React from "react";
import { Media, Button, Row, Col } from "reactstrap";
import { Link } from "react-router-dom";
import { useChat } from "../contexts/ChatContext";
import ReactImageFallback from "react-image-fallback";
import { onImgError } from "../helpers/ImageUtils";
import { useGroup } from "../contexts/GroupContext";
import defaultAvatar from "../assets/images/default.png";
import { useContact } from "../contexts/ContactContext";
import { useModal } from "../contexts/ModalContext";
import { useAuth } from "../contexts/AuthContext";
import { useCall } from "../contexts/CallContext";
import { useConference } from "../contexts/ConferenceContext";
import ModalAddParticipant from './ModalAddParticipant';

interface IProps {
  chatProfileSidebarOpen: boolean;
  setChatProfileSidebarOpen(value: boolean): void;
}

const ChatHead: React.FC<IProps> = ({
  chatProfileSidebarOpen,
  setChatProfileSidebarOpen,
}) => {
  const { user } = useAuth();
  const { chats, activeChat, setChats, setShowChat } = useChat();
  const { groups, setGroups } = useGroup();
  const { contacts, setContacts } = useContact();
  const { conferenceList } = useConference();
  const { setShowModalAddMember, setShowModalAddParticipant } = useModal();
  const { setCallNumber } = useCall();

  const ChatProfileSidebar = () => {
    setChatProfileSidebarOpen(!chatProfileSidebarOpen);
  };

  const makeCall = (useVideo: boolean) => {
    let contact = contacts.find((item) => item.jid === activeChat.jid);
    let group = groups.find((gr) => gr.jid === activeChat.jid);
    let conference = conferenceList.find((conf) => conf.jid === activeChat.jid);
    let number;
    if (contact) number = contact?.number;
    else if (group)
      number = `citrus-conference-authenticated-${group?.groupId}`;
    else if (conference) {
      if (
        conference.conferenceRoomParticipants.find(
          (part) => part.userUuid === user.id
        )?.userModerator
      )
        number = `citrus-conference_${conference.id}_${conference.moderatorPin}`;
      else
        number = `citrus-conference_${conference.id}_${conference.participantPin}`;
    }
    if (number) {
      setCallNumber({
        number,
        useAudio: true,
        useVideo,
      });
    }
  };
  function closeUserChat() {
    setShowChat(false);
  }
  const isAdmin =
    activeChat.members?.find((member) => member.userId === user.id)?.role ===
    "admin";

  return (
    <React.Fragment>
      <ModalAddParticipant/>
       <div className="p-3 p-lg-4 border-bottom">
        <Row className="align-items-center">
          <Col sm={4} xs={8}>
            <Media className="align-items-center">
              <div className="d-block d-lg-none mr-2">
                <Link
                  to="#"
                  onClick={() => closeUserChat()}
                  className="user-chat-remove text-muted font-size-16 p-2"
                >
                  <i className="ri-arrow-left-s-line"></i>
                </Link>
              </div>
              {activeChat.profilePicture ? (
                <div className="mr-3">
                  <ReactImageFallback
                    src={activeChat.profilePicture}
                    onError={() => {
                      if (activeChat.jid.includes("@conference."))
                        onImgError(
                          chats,
                          groups,
                          contacts,
                          setChats,
                          setGroups,
                          setContacts,
                          activeChat
                        );
                    }}
                    style={{ maxHeight: "40px", maxWidth: "40px" }}
                    fallbackImage={defaultAvatar}
                    className="rounded-circle avatar-lg img-thumbnail"
                    alt="chatvia"
                  />
                </div>
              ) : (
                <div className="chat-user-img align-self-center mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {activeChat.name.trim().charAt(0)}
                    </span>
                  </div>
                </div>
              )}

              <Media body className="overflow-hidden">
                <h5 className="font-size-16 mb-0 text-truncate">
                  <Link
                    to="#"
                    onClick={() => ChatProfileSidebar()}
                    className="text-reset user-profile-show"
                  >
                    {activeChat.name}
                  </Link>
                  {activeChat.groupId && 
                    <div style={{display: "flex", flexDirection: "row", width: '100%', backgroundColor: "yellow", textOverflow: "ellipsis", whiteSpace: "nowrap", overflow: "hidden"}}>
                      {activeChat.members?.map((member) => {
                        return(
                          <span className={"mr-1"} style={{
                            fontWeight: "lighter",
                            color: "gray",
                            fontSize: 12,
                            marginTop: 5,
                            marginLeft: 1,
                          }}>
                            {member.name}{";"}    
                          </span>
                        )
                      })}
                    </div>
                    }
                  {(() => {
                    switch (activeChat.status) {
                      case "online":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-success d-inline-block ml-1"></i>
                          </>
                        );

                      case "away":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-warning d-inline-block ml-1"></i>
                          </>
                        );

                      case "offline":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-secondary d-inline-block ml-1"></i>
                          </>
                        );

                      default:
                        return;
                    }
                  })()}
                </h5>
              </Media>
            </Media>
          </Col>
          <Col sm={8} xs={4}>
            <ul className="list-inline user-chat-nav text-right mb-0">
              {activeChat.jid.includes("@conference.") && isAdmin && (
                <li className="list-inline-item d-none d-lg-inline-block">
                  <Button
                    type="button"
                    color="none"
                    onClick={() => setShowModalAddMember(true)}
                    className="nav-btn user-profile-show"
                  >
                    <i className="ri-user-add-line"></i>
                  </Button>
                </li>
              )}
              {
                activeChat.conferenceRoomParticipants && (
                  <li className="list-inline-item d-none d-lg-inline-block">
                    <Button
                      type="button"
                      color="none"
                      onClick={() => {
                        setShowModalAddParticipant(true)
                      }}
                      className="nav-btn user-profile-show"
                    >
                      <i className="ri-user-add-line"></i>
                    </Button>
                  </li>
                )
              }
              <li className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    makeCall(false);
                  }}
                  className="nav-btn user-profile-show"
                >
                  <i className="ri-phone-line"></i>
                </Button>
              </li>
              <li className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    makeCall(true);
                  }}
                  className="nav-btn user-profile-show"
                >
                  <i className="ri-vidicon-line"></i>
                </Button>
              </li>
              <li className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => ChatProfileSidebar()}
                  className="nav-btn user-profile-show"
                >
                  <i className="ri-menu-line"></i>
                </Button>
              </li>
            </ul>
          </Col>
        </Row>
      </div>
    </React.Fragment>
  );
};

export default ChatHead;

