import React from "react";
import { Media } from "reactstrap";
import { Link } from "react-router-dom";
import SimpleBar from "simplebar-react";
import { IConference } from "../alias/IConference";
import { useConference } from "../contexts/ConferenceContext";

interface IPros {
  recentConferenceList: IConference[];
}

const ConferenceList: React.FC<IPros> = ({ recentConferenceList }) => {
  const { openConference } = useConference();

  return (
    <SimpleBar
      style={{ maxHeight: "100%" }}
      className="p-4 chat-message-list chat-group-list"
    >
      <ul className="list-unstyled chat-list">
        {recentConferenceList.map((conference, key) => (
          <li key={key}>
            <Link to="#" onClick={() => openConference(conference)}>
              <Media className="align-items-center">
                <div className="chat-user-img mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {conference.name.trim().toUpperCase().charAt(0)}
                    </span>
                  </div>
                </div>
                <Media body className="overflow-hidden">
                  <h5 className="text-truncate font-size-14 mb-0">
                    {conference.name.trim()}
                  </h5>
                  <p className="chat-user-message text-truncate mb-0">
                    {conference.startDatetime.trim()}
                  </p>
                </Media>
              </Media>
            </Link>
          </li>
        ))}
      </ul>
    </SimpleBar>
  );
};

export default ConferenceList;
