import React, { useState } from "react";
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Row,
  Col,
  Card,
} from "reactstrap";
import classnames from "classnames";
import { useTranslation } from "react-i18next";
import MediaTabFiles from "./MediaTabFiles";
import DocumentTabFiles from "./DocumentTabFiles";
import { useChat } from "../contexts/ChatContext";

const AttachedFilesOnSidebar: React.FC = () => {
  const [activeTab, setActiveTab] = useState("1");
  const { activeChat } = useChat();
  const { t } = useTranslation();
  return (
    <div className="mt-2">
      <Nav tabs>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === "1" })}
            onClick={() => {
              setActiveTab("1");
            }}
          >
            {t("Media")}
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === "2" })}
            onClick={() => {
              setActiveTab("2");
            }}
          >
            {t("Documents")}
          </NavLink>
        </NavItem>
      </Nav>
      <TabContent activeTab={activeTab}>
        <TabPane tabId="1">
          <Row>
            <Col sm="12">
              {activeChat.attachedFileList ? (
                <MediaTabFiles
                  list={activeChat.attachedFileList || undefined}
                />
              ) : (
                <Card className="p-3 border mb-2 d-flex flex-row flex-wrap">
                  <div className="text-muted">{t("Empty")}</div>
                </Card>
              )}
            </Col>
          </Row>
        </TabPane>
        <TabPane tabId="2">
          <Row>
            <Col sm="12">
              {activeChat.attachedFileList ? (
                <DocumentTabFiles
                  list={activeChat.attachedFileList || undefined}
                />
              ) : (
                <Card className="p-3 border mb-2 d-flex flex-row flex-wrap">
                  <div className="text-muted">{t("Empty")}</div>
                </Card>
              )}
            </Col>
          </Row>
        </TabPane>
      </TabContent>
    </div>
  );
};

export default AttachedFilesOnSidebar;
