import React from 'react';
import { useTranslation } from 'react-i18next';
import { Button } from 'reactstrap';
import Swal from 'sweetalert2';
import IChat from '../alias/IChat';
import IMember from '../alias/IMember';
import { useGroup } from '../contexts/GroupContext';
interface IProps{
    activeChat: IChat;
    actualMember: IMember | undefined;
}

const ChangeGroupNameButton : React.FC<IProps> = ({ activeChat, actualMember }) => {
    const { t } = useTranslation();
    const { setGroupIdOfLeaveRequest, setGroupJid } = useGroup();
    const { setMemberQuit } = useGroup();
    return <div className="px-3 px-lg-4 pt-3 pt-lg-4">
    <div className="user-chat-nav text-right">
      <Button
        color="none"
        type="button"
        onClick={() =>
          Swal.fire({
            title: t("Leave the group?"),
            text: "",
            icon: "warning",
            showCancelButton: true,
            confirmButtonColor: "#4e5e96",
            cancelButtonColor: "#d33",
            cancelButtonText: t("No"),
            confirmButtonText: t("Yes"),
            customClass: {
              popup: "swal-small-popup"
            }
          }).then((result) => {
            if (result.isConfirmed) {
              Swal.fire({
                position: 'center',
                icon: 'success',
                title: "You left the group!",
                showConfirmButton: false,
                timer: 1500,
                customClass: {
                  popup: "swal-small-popup"
                }
              })
              activeChat.groupId &&
              setGroupJid(activeChat.jid);
              setGroupIdOfLeaveRequest(activeChat.groupId as string);
              setMemberQuit(actualMember);
            }
          })
        }
        className="nav-btn"
        id="user-profile-hide"
      >
        <i className="ri-logout-box-line"></i>
      </Button>
    </div>
  </div>
}

export default ChangeGroupNameButton;
