import React, { useEffect } from "react";
import { Card, CardBody, CardTitle } from "reactstrap";
import { useChat } from "../contexts/ChatContext";
import { activeChat } from "../data/chat";
import CustomCollapse from "./CustomCollapse";
interface IProps {
  membersArrayPreventingDuplicateDisplayMember: any;
  toggleCollapse3: any;
  isOpen3: any;
}

const FavoritedMessages: React.FC<IProps> = ({
  membersArrayPreventingDuplicateDisplayMember,
  isOpen3,
  toggleCollapse3,
}) => {
  const { setFlagToLoadFavoriteMessages, favoriteMessages } = useChat();
  useEffect(() => {
    setFlagToLoadFavoriteMessages({
      contactType: activeChat.chatType as string,
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  
  useEffect(() => {

  })
  return (
    <Card className="mb-1 shadow-none border">
      <CustomCollapse
        title={`Favorited messages`}
        iconClass="ri-star-line"
        isOpen={isOpen3}
        toggleCollapse={toggleCollapse3}
      >
        
        {favoriteMessages?.map((item, key) => 
          <Card key={key}>
            <CardTitle>
              {item.messageDate}
            </CardTitle>
            <CardBody>
              {item.message}
            </CardBody>
          </Card>
          )
        }
      </CustomCollapse>
    </Card>
  );
};

export default FavoritedMessages;
