import API from "@digivox/apicommunicator";
import { DeleteMessageCallback, DeleteMessagePayload, FavoriteMessageCallback, FavoriteMessagePayload, ListFavoriteMessagesCallback } from "@digivox/apicommunicator/dist/types/types/types";
import IChat from "../../alias/IChat";
import IMessage from "../../alias/IMessage";
import { parseMessage } from "../utils/parseUtils";
import ContactsBusiness from "./Contacts";
import GroupsBusiness from "./Groups";
import UserBusiness from "./User";

interface GetMessagesCallback {
  (messages: IMessage[]): void;
}
export default class MessagesBusiness {
  constructor(
    private api: API,
    private contacts: ContactsBusiness,
    private groups: GroupsBusiness,
    private user: UserBusiness
  ) {}
  list = (chat: IChat, callback: GetMessagesCallback) => {
    if (!this.api) return;
    const lastMessageId = chat.messages[0]?.id;
    this.api.getMessages(
      chat.id,
      chat.jid.includes("@conference.") ? "groupchat" : "chat",
      { paramsUrl: { startingByMessageId: lastMessageId } },
      (data) => {
        if (data)
          this.user.get((user) => {
            callback(
              data.messages.map((msg) =>
                parseMessage(
                  msg,
                  this.contacts.getContacts(),
                  this.groups.getGroups(),
                  user
                )
              )
            );
          });
        else return;
      }
    );
  };
  save = (payload: FavoriteMessagePayload, callback: FavoriteMessageCallback) => {
    if(!this.api) return;
    this.api.favoriteMessage(payload, callback);
  }
  listFavorites = (payload: FavoriteMessagePayload, callback: ListFavoriteMessagesCallback) => {
    if(!this.api) return;
    this.api.listFavoriteMessages({paramsUrl: {contactType: payload.contactType}}, callback);
  }
  deleteFavorite = (payload: DeleteMessagePayload, callback: DeleteMessageCallback) => {
    if(!this.api) return;
    this.api.deleteMessage({messageId: payload.messageId}, callback);
  }
}
