import React from 'react';
import IMessage from '../alias/IMessage';
import { useAuth } from '../contexts/AuthContext';
import { useContact } from '../contexts/ContactContext';

interface IGroupNotification{
    msg: IMessage;
}

const GroupNotification : React.FC<IGroupNotification> = ({ msg }) => {
    const { contacts, contactsOutsideUserContacts } = useContact();
    const { user } = useAuth();

    switch(msg.eventId){
        case 6:
        let userThatIsRemoving: any;
        let userRemoved: any;
        contacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsRemoving = ctc.name;
            };
            if(ctc.id === msg.fromUser.split('@')[0] || ctc.id === msg.toUser.split('@')[0]){
                userRemoved = ctc.name
            }
        })
        contactsOutsideUserContacts.forEach((ctc) => {
            if(ctc.id === msg.eventBody?.split('@')[0]){
                userThatIsRemoving = ctc.name;
            }
            if(ctc.id === msg.fromUser.split('@')[0] || ctc.id === msg.toUser.split('@')[0]){
                userRemoved = ctc.name
            }
        })
        if(!userRemoved){
            userRemoved = user.name;
        }
        return(
            <div className="card">
                <div className="card-body" style={{color: "red"}}>
                {userThatIsRemoving} has been removed by {userRemoved}!
                </div>
            </div>
        );
        default:
            return <></>    
    }
}

export default GroupNotification;
