import { toast } from 'react-toastify';

export const toastSuccess = (label: string) => {
  toast.success(`${label}`, {
    position: "top-center",
    autoClose: 3000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
  });  
}

export const toastError = (label: string) => {
  toast.error(`${label}`, {
    position: "top-center",
    autoClose: 3000,
    hideProgressBar: false,
    closeOnClick: true,
    pauseOnHover: true,
    draggable: true,
    progress: undefined,
  });  
}
