import React, { createContext, useContext, useState, useEffect } from "react";
import ICall from "../alias/ICall";
import { UpdateConstraintsParams } from "../services/SquadVoiceCommunicator";
//alias
interface MakeCall {
  number: string;
  useVideo: boolean;
  useAudio: boolean;
}
interface ICallContextData {
  show: boolean;
  setShow(value: boolean): void;
  currentCalls: Map<string, ICall>;
  setCurrentCalls(value: Map<string, ICall>): void;
  callNumber: MakeCall | undefined;
  setCallNumber(value: MakeCall | undefined): void;
  updateConstraints: UpdateConstraintsParams | undefined;
  setUpdateConstraints(val: UpdateConstraintsParams | undefined): void;
}

export const CallContext = createContext<ICallContextData>(
  {} as ICallContextData
);

export const CallProvider: React.FC = ({ children }) => {
  const [show, setShow] = useState(false);
  const [currentCalls, setCurrentCalls] = useState<Map<string, ICall>>(
    new Map<string, ICall>()
  );
  const [callNumber, setCallNumber] = useState<MakeCall | undefined>();
  const [
    updateConstraints,
    setUpdateConstraints,
  ] = useState<UpdateConstraintsParams>();
  const time = () => {
    // const callWhitoutTime = Array.from(currentCalls.values()).find(
    //   (c) => !c.interval
    // );
    // if (callWhitoutTime) {
    //   if (callWhitoutTime.callStatus === CallStatus.ON_GOING) {
    //     let cc = { ...callWhitoutTime };
    //     if (cc.interval) clearInterval(cc.interval);
    //     cc.interval = setInterval(() => {
    //       const currentCallsCopy = new Map<string, ICall>(currentCalls);
    //       cc.seconds = cc.seconds ? cc.seconds + 1 : 1;
    //       currentCallsCopy.set(cc.callId, cc);
    //       setCurrentCalls(currentCallsCopy);
    //     }, 1000);
    //   }
    // }
  };

  useEffect(time, [currentCalls]);

  return (
    <CallContext.Provider
      value={{
        show,
        setShow,
        currentCalls,
        setCurrentCalls,
        callNumber,
        setCallNumber,
        updateConstraints,
        setUpdateConstraints,
      }}
    >
      {children}
    </CallContext.Provider>
  );
};

export function useCall() {
  return useContext(CallContext);
}
