import { ConferenceRoomParticipant } from "@digivox/apicommunicator/dist/types/types/types";
import React, { createContext, useContext, useState } from "react";
import IChat from "../alias/IChat";
import { IConference } from "../alias/IConference";
import ICreateConference from "../alias/ICreateConference";
import ChatType from "../enuns/ChatType";
import ScreenType from "../enuns/ScreenType";
import { useChat } from "./ChatContext";
import { useMainBox } from "./MainBoxContext";

interface IAddingParticipantsPayload {
  conferenceId: string;
  data: ConferenceRoomParticipant[];
}

interface IRemovingParticipantsPayload {
  conferenceId: string;
  participantId: string;
}

interface IConferenceContextData {
  createConference: ICreateConference | undefined;
  setCreateConference(value: ICreateConference | undefined): void;
  editConference: IConference | undefined;
  setEditConference(value: IConference | undefined): void;
  deleteConference: IConference | undefined;
  setDeleteConference(value: IConference | undefined): void;
  listRooms: boolean;
  setListRooms(val: boolean): void;
  conferenceList: IConference[];
  setConferenceList(list: IConference[]): void;
  openConference(value: IConference): void;
  conferenceToUpdate: IConference | undefined;
  setConferenceToUpdate(value: IConference | undefined): void;
  conferenceToRemove: IConference | undefined;
  setConferenceToRemove(value: IConference | undefined): void;
  checkedParticipants: any;
  setCheckedParticipants(value: any): void;
  conferenceToInsertMember: any;
  setConferenceToInsertMember(value: any): void;
  addingParticipantsPayload: IAddingParticipantsPayload | undefined;
  setAddingParticipantsPayload(
    value: IAddingParticipantsPayload | undefined
  ): void;
  removingParticipantsPayload: IRemovingParticipantsPayload | undefined;
  setRemovingParticipantsPayload(
    value: IRemovingParticipantsPayload | undefined
  ): void;
  activeConference: IConference | undefined;
  updateConference(value: IConference): void;
}

export const ConferenceContext = createContext<IConferenceContextData>(
  {} as IConferenceContextData
);

export const ConferenceProvider: React.FC = ({ children }) => {
  const [listRooms, setListRooms] = useState<boolean>(false);
  const [conferenceList, setConferenceList] = useState<IConference[]>([]);
  const [createConference, setCreateConference] = useState<ICreateConference>();
  const [editConference, setEditConference] = useState<IConference>();
  const [deleteConference, setDeleteConference] = useState<IConference>();
  const [conferenceToUpdate, setConferenceToUpdate] = useState<IConference>();
  const [conferenceToRemove, setConferenceToRemove] = useState<IConference>();
  const [checkedParticipants, setCheckedParticipants] = useState();
  const [conferenceToInsertMember, setConferenceToInsertMember] = useState();
  const [addingParticipantsPayload, setAddingParticipantsPayload] = useState<
    IAddingParticipantsPayload | undefined
  >(undefined);
  const [
    removingParticipantsPayload,
    setRemovingParticipantsPayload,
  ] = useState<IRemovingParticipantsPayload | undefined>(undefined);
  const { activeChat, chats, setChats, setActiveChat } = useChat();
  const { setScreen } = useMainBox();
  const activeConference: IConference | undefined = conferenceList.find(
    (cfr) => cfr.id === activeChat.id
  );

  const openConference = (obj: IConference) => {
    if (activeChat?.jid === obj.jid) return;
    updateConference(obj);
  };

  const updateConference = (obj: IConference) => {
    let find = false;

    setChats(
      chats.map((item) => {
        if (item.jid === obj.jid) {
          setActiveChat(item);
          item.unRead = 0;
          item.mencioned = 0;
          if (item.messageLoaded !== null)
            item.messageLoaded = item.messages.length >= 20 ? false : true;
          setScreen(ScreenType.CHAT);
          find = true;
        }
        return item;
      })
    );

    if (!find) {
      let newChat: IChat = {
        id: obj.jid.split("@")[0],
        jid: obj.jid,
        name: obj.name,
        messageLoaded: true,
        unRead: 0,
        chatType: ChatType.CONFERENCE,
        inputMessage: "",
        isTyping: false,
        messages: [],
        favorite: false,
        added: true,
        conferenceRoomParticipants: obj.conferenceRoomParticipants,
        mencioned: 0,
      };
      setScreen(ScreenType.CHAT);
      setActiveChat(newChat);
    }
  }



  return (
    <ConferenceContext.Provider
      value={{
        createConference,
        setCreateConference,
        editConference,
        setEditConference,
        deleteConference,
        setDeleteConference,
        conferenceList,
        setConferenceList,
        listRooms,
        setListRooms,
        openConference,
        conferenceToUpdate,
        setConferenceToUpdate,
        conferenceToRemove,
        setConferenceToRemove,
        checkedParticipants,
        setCheckedParticipants,
        conferenceToInsertMember,
        setConferenceToInsertMember,
        addingParticipantsPayload,
        setAddingParticipantsPayload,
        removingParticipantsPayload,
        setRemovingParticipantsPayload,
        activeConference,
        updateConference
      }}
    >
      {children}
    </ConferenceContext.Provider>
  );
};

export function useConference() {
  return useContext(ConferenceContext);
}
